import React from "react";

const LANG_SKILLS = [
    { lang: "Java", lvl: 4 },
    { lang: "Python", lvl: 3 },
    { lang: "HTML/CSS/JS", lvl: 3 },
    { lang: "SQL", lvl: 1.5 },
    { lang: "C", lvl: 1 },
    { lang: "GoLang", lvl: 1 },
    { lang: "VHDL", lvl: 1 }
];

const FRAMEWORKS = ["React", "Django", "ASP.NET MVC", "Node.js", "JSP", "Bootstrap"];
const DEVTOOLS = ["Git", "Jira", "MySQL", "MongoDB", "VSCode", "Figma"];

export default function AboutPage() {
    return (
        <div style={{ maxWidth: 700, margin: "60px auto", color: "#232751", padding: "0 12px" }}>
            <h1 style={{ fontWeight: 900, fontSize: 38, color: "#7B9ACC" }}>About Me</h1>
            <p style={{ fontSize: 18, margin: "34px 0", color: "#334" }}>
                Iâ€™m really interested in front-end development because I enjoy turning ideas and designs into something interactive and alive. I like the balance between logic and creativity, writing code that not only works but also looks clean and intuitive. When I started learning HTML and CSS, I realised I actually enjoy making things functional rather than only focusing on the visual side. But I still value good design, so I always try to follow design principles and make sure my code represents the original concept properly.
            </p>

            <h2 style={{ fontSize: 24, fontWeight: 700, marginTop: 32 }}>Languages</h2>
            <div style={{ margin: "18px 0" }}>
                {LANG_SKILLS.map(val => (
                    <div key={val.lang} style={{ marginBottom: 15 }}>
                        <span style={{ minWidth: 95, display: "inline-block", fontWeight: 600 }}>{val.lang}:</span>
                        <span style={{
                            display: "inline-block", marginLeft: 12, height: 23, verticalAlign: "middle",
                            background: "#D0DEFA", borderRadius: 14, minWidth: 40
                        }}>
                            <div style={{
                                height: 23, borderRadius: 14, background: "#7B9ACC", width: `${val.lvl * 25}%`,
                                minWidth: 40, transition: "width .5s", color: "#fff", fontWeight: 700, textAlign: "center"
                            }}>{val.lvl} yr</div>
                        </span>
                    </div>
                ))}
            </div>

            <h2 style={{ fontSize: 24, fontWeight: 700, marginTop: 32 }}>Frameworks / Technologies</h2>
            <div style={{ marginBottom: 18 }}>
                {FRAMEWORKS.map(fw => (
                    <span key={fw} style={{
                        background: "#EDF2FB", color: "#22253B", borderRadius: 11,
                        padding: "7px 18px", marginRight: 10, marginBottom: 10, fontWeight: 600, fontSize: 15
                    }}>{fw}</span>
                ))}
            </div>
            <h2 style={{ fontSize: 24, fontWeight: 700, marginTop: 32 }}>Development Tools</h2>
            <div>
                {DEVTOOLS.map(fw => (
                    <span key={fw} style={{
                        background: "#EDF2FB", color: "#232751", borderRadius: 11,
                        padding: "7px 18px", marginRight: 10, marginBottom: 10, fontWeight: 600, fontSize: 15
                    }}>{fw}</span>
                ))}
            </div>
        </div>
    );
}
