import React, { useState, useEffect, useRef } from "react";

// === DATA DENGAN "less than a year" ===
const LANG_SKILLS = [
    { lang: "Java", lvl: 2 },
    { lang: "Python", lvl: 2 },
    { lang: "HTML/CSS/JS", lvl: 2 },
    { lang: "SQL", lvl: 1.5 },
    { lang: "C++", lvl: 1 },
];
const FRAMEWORKS = [
    { name: "React", lvl: 0.5 },
    { name: "Django", lvl: 0.5 },
    { name: "Node.js", lvl: 0.5 },
    { name: "JSP", lvl: 1 },
    { name: "Spring Boot", lvl: 0.5 },   // TAMBAHAN
    { name: "Vue.js", lvl: 0.5 },       // TAMBAHAN
];
const DEVTOOLS = [
    { name: "Git", lvl: 2 },
    { name: "Jira", lvl: 1.5 },
    { name: "MySQL", lvl: 2 },
    { name: "MongoDB", lvl: 0.5 },
    { name: "VSCode", lvl: 2 },
    { name: "Figma", lvl: 1 },
    { name: "Docker", lvl: 0.5 },
    { name: "Postman", lvl: 1 }
];
const SECTION_TABS = [
    { key: "languages", label: "Languages" },
    { key: "frameworks", label: "Frameworks / Tech" },
    { key: "tools", label: "Dev Tools" },
];

// Hook: Responsive
function useIsMobile(breakpoint = 640) {
    const [isMobile, setIsMobile] = useState(window.innerWidth < breakpoint);
    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < breakpoint);
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, [breakpoint]);
    return isMobile;
}

// === ANIMATED BAR DENGAN STAGGER & LABEL "less than a year" ===
function AnimatedBar({ value, label, color = "#7B9ACC", delay = 0 }) {
    const isMobile = useIsMobile();
    const [width, setWidth] = useState(0);
    const max = 2;

    useEffect(() => {
        const timer = setTimeout(() => {
            setWidth((value / max) * 100);
        }, 150 + delay * 100);
        return () => clearTimeout(timer);
    }, [value, max, delay]);

    // Format label: <1 tahun → "less than a year"
    const displayValue = value < 1 ? "less than a year" : `${value} yr${value > 1 ? "s" : ""}`;

    return (
        <div
            style={{
                background: "#E6EBF5",
                height: isMobile ? 36 : 40,
                borderRadius: 20,
                margin: isMobile ? "10px 0" : "8px 0",
                position: "relative",
                width: isMobile ? "92vw" : 320,
                maxWidth: isMobile ? "99vw" : 400,
                overflow: "hidden",
                boxShadow: "inset 0 1px 3px #00000012",
            }}
        >
            {/* Background Bar */}
            <div
                style={{
                    height: "100%",
                    width: `${width}%`,
                    background: `linear-gradient(90deg, ${color}dd, ${color})`,
                    borderRadius: 20,
                    transition: "width 1.1s cubic-bezier(0.34, 1.06, 0.64, 1)",
                    position: "absolute",
                    top: 0,
                    left: 0,
                }}
            />

            {/* Label di dalam bar */}
            <div
                style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    display: "flex",
                    alignItems: "center",
                    paddingLeft: isMobile ? 16 : 18,
                    fontWeight: 700,
                    fontSize: isMobile ? 14.5 : 16,
                    color: width > 30 ? "#fff" : "#232751",
                    zIndex: 2,
                    transition: "color 0.3s",
                }}
            >
                <span style={{ marginRight: 10, fontSize: isMobile ? 15 : 17 }}>
                    {label}
                </span>
                <span style={{ marginLeft: "auto", marginRight: 16, fontSize: isMobile ? 13 : 14 }}>
                    {displayValue}
                </span>
            </div>
        </div>
    );
}

// === MAIN COMPONENT ===
export default function AboutPage() {
    const [section, setSection] = useState("languages");
    const isMobile = useIsMobile();
    const containerRef = useRef();

    // Render skill list berdasarkan section
    const renderSkills = () => {
        let skills = [];
        if (section === "languages") skills = LANG_SKILLS;
        if (section === "frameworks") skills = FRAMEWORKS;
        if (section === "tools") skills = DEVTOOLS;

        return skills.map((item, i) => {
            const label = section === "languages" ? item.lang : item.name;
            const value = item.lvl;
            const colors = {
                languages: "#7B9ACC",
                frameworks: "#6A8BD6",
                tools: "#5A7BC5",
            };
            return (
                <AnimatedBar
                    key={label}
                    label={label}
                    value={value}
                    color={colors[section]}
                    delay={i}
                />
            );
        });
    };

    return (
        <div
            ref={containerRef}
            style={{
                maxWidth: isMobile ? "97vw" : 780,
                margin: isMobile ? "24px auto" : "70px auto",
                padding: isMobile ? "0 4vw" : "0 20px",
                fontFamily: "'Segoe UI', Arial, sans-serif",
                color: "#232751",
            }}
        >
            {/* Header */}
            <h1
                style={{
                    fontWeight: 900,
                    fontSize: isMobile ? 30 : 42,
                    background: "linear-gradient(90deg, #7B9ACC, #5A7BC5)",
                    WebkitBackgroundClip: "text",
                    WebkitTextFillColor: "transparent",
                    marginBottom: isMobile ? 18 : 28,
                    textAlign: isMobile ? "center" : "left",
                }}
            >
                About Me
            </h1>

            {/* Bio */}
            <p
                style={{
                    fontSize: isMobile ? 15.5 : 18,
                    lineHeight: isMobile ? 1.7 : 1.8,
                    color: "#334466",
                    textAlign: isMobile ? "justify" : "left",
                    margin: isMobile ? "18px 0 28px" : "32px 0 40px",
                    padding: isMobile ? "0 2px" : 0,
                }}
            >
                Software Development student with a passion for <strong>front-end development</strong>, where I blend creativity and logic to build clean, interactive, and user-focused interfaces. Proficient in <strong>HTML, CSS, JavaScript, and React</strong>, I enjoy translating designs into functional, responsive experiences while maintaining code quality and design fidelity.
                <br /><br />
                Currently expanding into <strong>backend development</strong> with <strong>Spring Boot</strong> and <strong>Vue.js</strong>, and exploring <strong>DevOps</strong> (Docker, CI/CD) to become a well-rounded full-stack engineer.
                <br /><br />
                Also diving into <strong>mobile apps (React Native)</strong>, <strong>QA testing</strong>, and <strong>business analysis</strong> — driven by curiosity to solve real-world problems from multiple angles.
            </p>

            {/* Tab Buttons */}
            <div
                style={{
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    justifyContent: "center",
                    gap: isMobile ? 12 : 20,
                    margin: isMobile ? "24px 0" : "44px 0",
                    width: "100%",
                }}
            >
                {SECTION_TABS.map((tab) => (
                    <button
                        key={tab.key}
                        onClick={() => setSection(tab.key)}
                        style={{
                            flex: isMobile ? "1" : "none",
                            background: section === tab.key ? "linear-gradient(135deg, #7B9ACC, #5A7BC5)" : "#F0F4FF",
                            color: section === tab.key ? "#fff" : "#232751",
                            fontWeight: 700,
                            fontSize: isMobile ? 15 : 17,
                            padding: isMobile ? "11px 20px" : "12px 32px",
                            border: "none",
                            borderRadius: 14,
                            boxShadow: section === tab.key ? "0 4px 16px #7B9ACC44" : "0 1px 3px #00000012",
                            transition: "all 0.22s ease",
                            cursor: "pointer",
                            minWidth: isMobile ? "auto" : 160,
                        }}
                        onMouseEnter={(e) => {
                            if (section !== tab.key) e.currentTarget.style.background = "#E1E8F7";
                        }}
                        onMouseLeave={(e) => {
                            if (section !== tab.key) e.currentTarget.style.background = "#F0F4FF";
                        }}
                    >
                        {tab.label}
                    </button>
                ))}
            </div>

            {/* Skill Bars Container */}
            <div
                style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    width: "100%",
                    minHeight: 300,
                    padding: isMobile ? "8px 0" : "12px 0",
                }}
            >
                {renderSkills()}
            </div>

            {/* Footer Note */}
            <div
                style={{
                    textAlign: "center",
                    marginTop: 32,
                    fontSize: 13,
                    color: "#7788AA",
                    fontStyle: "italic",
                }}
            >
                * Experience measured in years of active use
            </div>
        </div>
    );
}
