import React, { useState } from "react";
import { motion } from "framer-motion";

// Glass card utility with Tailwind & fallback style
function GlassCard({ className = "", children, ...props }) {
    return (
        <div
            className={`glass-card rounded-2xl border border-blue-100 shadow backdrop-blur-md ${className}`}
            style={{ backgroundColor: "rgba(255,255,255,0.68)", ...props.style }}
            {...props}
        >
            {children}
        </div>
    );
}

// Reusable section title with subtitle
function SectionTitle({ subtitle, children }) {
    return (
        <div className="mb-10 mt-2 text-center">
