// Lightbox modal for diagrams/videos
function LightboxModal({ open, onClose, children }) {
    if (!open) return null;
    return (
        <div className="fixed inset-0 bg-black/40 z-50 flex items-center justify-center">
            <div className="bg-white/80 rounded-2xl p-7 shadow-2xl w-[96vw] max-w-xl relative backdrop-blur-md">
