import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Code, Search, AlertTriangle, CheckCircle, ArrowRight, FileText, Users, Globe, Zap, Shield } from 'lucide-react';

const theme = {
    primary: '#5DADE2',
    light: '#A7C7E7',
    bg: '#F8FBFF',
    text: '#1A365D',
    white: '#FFFFFF',
    success: '#10B981',
    danger: '#EF4444',
    warning: '#F59E0B',
};

const fadeIn = { hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } };

export default function STQMFullShowcase() {
    const [activeTab, setActiveTab] = useState('overview');

    return (
        <div style={{
            minHeight: '100vh',
            background: theme.bg,
            fontFamily: "'Inter', sans-serif",
            color: theme.text,
        }}>
            {/* Hero */}
            <section style={{ padding: '80px 20px', textAlign: 'center', background: `linear-gradient(135deg, ${theme.light} 0%, ${theme.primary} 100%)`, color: 'white' }}>
                <motion.h1
                    initial={{ opacity: 0, y: -30 }}
                    animate={{ opacity: 1, y: 0 }}
                    style={{ fontSize: '3rem', fontWeight: 800, margin: 0 }}
                >
                    Systems Testing & Quality Management
                </motion.h1>
                <motion.p
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                    style={{ fontSize: '1.3rem', margin: '16px 0 32px', maxWidth: '800px', marginLeft: 'auto', marginRight: 'auto' }}
                >
                    Delivered 3 high-impact projects: Requirements Review & Risk Analysis, Black/White-Box Testing, and End-to-End System Automation — ensuring defect-free software delivery.
                </motion.p>

                <motion.div
                    initial={{ scale: 0.8 }}
                    animate={{ scale: 1 }}
                    transition={{ delay: 0.4 }}
                    style={{ display: 'flex', justifyContent: 'center', gap: '30px', flexWrap: 'wrap' }}
                >
                    <ImpactCard icon={<Shield />} value="3" label="Projects Delivered" />
                    <ImpactCard icon={<Zap />} value="85%" label="Test Coverage" />
                    <ImpactCard icon={<CheckCircle />} value="88%" label="Success Rate" />
                </motion.div>
            </section>

            {/* Navigation */}
            <div style={{ display: 'flex', justifyContent: 'center', gap: '12px', padding: '30px 20px', flexWrap: 'wrap' }}>
                {['overview', 'project1', 'project2', 'project3', 'impact'].map((tab) => (
                    <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        style={{
                            padding: '12px 28px',
                            border: 'none',
                            borderRadius: '50px',
                            background: activeTab === tab ? theme.primary : theme.white,
                            color: activeTab === tab ? 'white' : theme.text,
                            fontWeight: 600,
                            cursor: 'pointer',
                            boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
                            transition: '0.3s',
                        }}
                    >
                        {tab === 'project1' ? 'Project 1' : tab === 'project2' ? 'Project 2' : tab === 'project3' ? 'Project 3' : tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>

            {/* Content */}
            <div style={{ maxWidth: '1100px', margin: '0 auto 80px', padding: '0 20px' }}>
                {activeTab === 'overview' && <Overview />}
                {activeTab === 'project1' && <Project1 />}
                {activeTab === 'project2' && <Project2 />}
                {activeTab === 'project3' && <Project3 />}
                {activeTab === 'impact' && <Impact />}
            </div>

            {/* CTA Footer */}
            <motion.footer
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                style={{
                    background: theme.primary,
                    color: 'white',
                    textAlign: 'center',
                    padding: '40px 20px',
                }}
            >
                <h3 style={{ margin: '0 0 16px', fontSize: '1.5rem' }}>Proven Testing Expertise</h3>
                <p style={{ margin: '0 0 24px', opacity: 0.9 }}>From requirements to automation — ready to drive quality in your team.</p>
                <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    style={{
                        background: 'white',
                        color: theme.primary,
                        border: 'none',
                        padding: '14px 32px',
                        borderRadius: '50px',
                        fontWeight: 600,
                        fontSize: '1.1rem',
                        cursor: 'pointer',
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '8px',
                    }}
                >
                    View Full Portfolio <ArrowRight size={20} />
                </motion.button>
            </motion.footer>
        </div>
    );
}

// Components
function ImpactCard({ icon, value, label }) {
    return (
        <motion.div
            whileHover={{ y: -8 }}
            style={{
                background: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                padding: '20px 30px',
                borderRadius: '16px',
                minWidth: '160px',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '2.5rem', marginBottom: '8px' }}>{icon}</div>
            <div style={{ fontSize: '2rem', fontWeight: 800 }}>{value}</div>
            <div style={{ fontSize: '0.9rem', opacity: 0.9 }}>{label}</div>
        </motion.div>
    );
}

function Overview() {
    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Full Testing Lifecycle Coverage</h2>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '24px', marginTop: '30px' }}>
                <FeatureCard
                    icon={<FileText />}
                    title="Project 1: Verification/Validation & Risk"
                    desc="Static review + FMEA: 40 issues identified pre-code."
                />
                <FeatureCard
                    icon={<Code />}
                    title="Project 2: Black/White-Box Testing"
                    desc="Partitioning, boundary, coverage techniques on binary search."
                />
                <FeatureCard
                    icon={<Globe />}
                    title="Project 3: System Testing"
                    desc="Automated E2E tests on e-commerce site with TestCafe/Selenium."
                />
            </div>
        </motion.div>
    );
}

function Project1() {
    const defects = [
        { id: 'ST001', cat: 'Security', issue: 'Password rules undefined', tags: ['Incomplete'] },
        { id: 'ST013', cat: 'Process', issue: 'Advisor approval conflict', tags: ['Inconsistent'] },
    ];

    const risks = [
        { id: 'RS018', cat: 'Localization', fail: 'No non-English support', rpn: 20 },
        { id: 'RS001', cat: 'Security', fail: 'Email delay → user deleted', rpn: 6 },
    ];

    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Project 1: Verification/Validation, Software Review, Risk Analysis</h2>
            <p style={{ margin: '20px 0', fontSize: '1.1rem' }}>Applied V&V to identify 20 defects and 20 risks in requirements specs.</p>
            <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
                <div style={{ flex: 1 }}>
                    <h3>Sample Defects</h3>
                    <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
                        <thead><tr style={{ background: theme.primary, color: 'white' }}><th style={th}>ID</th><th style={th}>Issue</th></tr></thead>
                        <tbody>
                            {defects.map(d => <tr key={d.id}><td style={td}>{d.id}</td><td style={td}>{d.issue}</td></tr>)}
                        </tbody>
                    </table>
                </div>
                <div style={{ flex: 1 }}>
                    <h3>Sample Risks (FMEA)</h3>
                    <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
                        <thead><tr style={{ background: theme.accent, color: 'white' }}><th style={th}>ID</th><th style={th}>RPN</th></tr></thead>
                        <tbody>
                            {risks.map(r => <tr key={r.id}><td style={td}>{r.id} - {r.fail}</td><td style={td}><Badge text={r.rpn} color={r.rpn > 10 ? theme.danger : theme.success} /></td></tr>)}
                        </tbody>
                    </table>
                </div>
            </div>
        </motion.div>
    );
}

function Project2() {
    const blackbox = [
        { method: 'Input Partitioning', tests: 8, desc: 'Covered null, empty, valid/invalid arrays' },
        { method: 'Boundary Value', tests: 10, desc: 'Tested edges like size=100/101, min/max values' },
    ];

    const whitebox = [
        { method: 'Statement Coverage', tests: 4, desc: '100% statements executed' },
        { method: 'Path Coverage', tests: 5, desc: 'All paths: loops 0/1/multiple times' },
    ];

    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Project 2: Black-Box & White-Box Testing</h2>
            <p style={{ margin: '20px 0', fontSize: '1.1rem' }}>Designed 37 test cases for binary search algorithm using partitioning, boundary, and coverage techniques.</p>
            <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
                <div style={{ flex: 1 }}>
                    <h3>Black-Box Techniques</h3>
                    {blackbox.map(m => <MethodCard key={m.method} {...m} />)}
                </div>
                <div style={{ flex: 1 }}>
                    <h3>White-Box Techniques</h3>
                    {whitebox.map(m => <MethodCard key={m.method} {...m} />)}
                </div>
            </div>
        </motion.div>
    );
}

function MethodCard({ method, tests, desc }) {
    return (
        <motion.div
            whileHover={{ scale: 1.02 }}
            style={{
                background: theme.white,
                padding: '15px',
                borderRadius: '12px',
                marginBottom: '15px',
                boxShadow: '0 4px 15px rgba(0,0,0,0.05)',
            }}
        >
            <h4 style={{ margin: 0 }}>{method}</h4>
            <p style={{ fontSize: '0.9rem', color: '#64748B' }}>{tests} tests: {desc}</p>
        </motion.div>
    );
}

function Project3() {
    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Project 3: System Testing</h2>
            <p style={{ margin: '20px 0', fontSize: '1.1rem' }}>Group project: Automated testing of e-commerce site (Poco Mega Store) with TestCafe & Selenium.</p>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px' }}>
                <FeatureCard icon={<Users />} title="Team Role" desc="Tested search, login fail, contact form" />
                <FeatureCard icon={<Code />} title="Tools" desc="TestCafe (JS), Selenium (Python)" />
                <FeatureCard icon={<CheckCircle />} title="Results" desc="9 tests, 88.89% pass, 85% coverage" />
            </div>
            <h3 style={{ marginTop: '30px' }}>Key Functions Tested</h3>
            <ul style={{ listStyle: 'none', padding: 0 }}>
                <li>Registration, Login/Logout, Search</li>
                <li>Add/View Cart, Checkout, Contact</li>
            </ul>
        </motion.div>
    );
}

function Impact() {
    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Overall Impact</h2>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))', gap: '24px', marginTop: '30px' }}>
                <ImpactBox title="Bugs Prevented" value="60+" desc="Across lifecycle" />
                <ImpactBox title="Coverage Achieved" value="85%" desc="Functional & code" />
                <ImpactBox title="Efficiency Gain" value="60%" desc="Via automation" />
            </div>
        </motion.div>
    );
}

function ImpactBox({ title, value, desc }) {
    return (
        <motion.div
            whileHover={{ scale: 1.03 }}
            style={{
                background: `linear-gradient(135deg, ${theme.light} 0%, ${theme.primary} 100%)`,
                color: 'white',
                padding: '30px',
                borderRadius: '16px',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '3rem', fontWeight: 800, marginBottom: '8px' }}>{value}</div>
            <h4 style={{ margin: '0 0 8px' }}>{title}</h4>
            <p style={{ margin: 0, fontSize: '0.9rem', opacity: 0.9 }}>{desc}</p>
        </motion.div>
    );
}

function FeatureCard({ icon, title, desc }) {
    return (
        <motion.div
            whileHover={{ y: -8 }}
            style={{
                background: theme.white,
                padding: '24px',
                borderRadius: '16px',
                boxShadow: '0 8px 25px rgba(0,0,0,0.08)',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '2.5rem', color: theme.primary, marginBottom: '16px' }}>{icon}</div>
            <h3 style={{ margin: '0 0 12px', fontSize: '1.2rem' }}>{title}</h3>
            <p style={{ margin: 0, color: '#64748B', fontSize: '0.95rem' }}>{desc}</p>
        </motion.div>
    );
}

function Badge({ text, color = theme.primary }) {
    return (
        <span style={{
            background: color,
            color: 'white',
            padding: '4px 12px',
            borderRadius: '50px',
            fontSize: '0.75rem',
            fontWeight: 600,
        }}>
            {text}
        </span>
    );
}

// Styles
const sectionStyle = { background: theme.white, padding: '40px', borderRadius: '20px', boxShadow: '0 10px 40px rgba(0,0,0,0.08)' };
const titleStyle = { fontSize: '2rem', fontWeight: 700, textAlign: 'center', margin: '0 0 10px', color: theme.text };
const th = { padding: '16px', textAlign: 'left', fontWeight: 600 };
const td = { padding: '16px', borderBottom: `1px solid #E2E8F0` };
