export default function HomePage() {
    const isMobile = useIsMobile();
    const heroTxt = "Hi, I'm Yafie, an aspiring Software Engineer & Business Analyst.";
    const animated = useLoopTyping(heroTxt, isMobile ? 27 : 40, 1700);
    const navigate = useNavigate();

    return (
        <div style={{
            minHeight: isMobile ? "78vh" : "88vh",
            background: "transparent",  // <-- GANTI INI jadi transparent biar particles tembus
            // atau kalau mau subtle overlay: "rgba(249, 250, 252, 0.4)", tapi mulai dari transparent dulu
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100vw",
            padding: isMobile ? "0 2vw" : 0,
            position: "relative",  // tambah ini biar positioning aman
            zIndex: 2,  // pastiin konten di atas particles
        }}>
            {/* Tambah backdrop blur ke teks container kalau teks kurang kontras */}
            <div style={{
                background: "rgba(255, 255, 255, 0.65)",  // semi-transparan putih biar teks pop di atas particles
                backdropFilter: "blur(12px)",  // efek frosted glass modern
                borderRadius: "24px",
                padding: isMobile ? "2rem 1.5rem" : "3rem 4rem",
                boxShadow: "0 8px 32px rgba(123, 154, 204, 0.15)",
                maxWidth: isMobile ? "90%" : "70%",
                textAlign: "center",
            }}>
                <div style={{
                    fontWeight: 900,
                    fontSize: isMobile ? 31 : 44,
                    color: "#7B9ACC",
                    letterSpacing: -1.4,
                    minHeight: isMobile ? 40 : 65,
                }}>
                    {animated}
                </div>
                <div style={{
                    margin: isMobile ? "22px 0 10px" : "40px 0 18px",
                    fontSize: isMobile ? 15 : 22,
                    color: "#232751",
                    opacity: .88,
                }}>
                    Bachelor of Information Technology | Open for 2025&2026 Internship <br />
                    <span style={{ fontSize: isMobile ? 13 : 17, opacity: .67 }}>
                        Enterprise Software Development & Business Information System Management @UTS.
                    </span>
                </div>
                <button
                    onClick={() => navigate("/contact")}
                    style={{
                        marginTop: isMobile ? 16 : 28,
                        background: "#7B9ACC",
                        color: "#fff",
                        fontWeight: 800,
                        fontSize: isMobile ? 14 : 18,
                        border: 0,
                        borderRadius: 23,
                        padding: isMobile ? "11px 22vw" : "15px 46px",
                        boxShadow: "0 2px 18px #d2ddec33",
                        cursor: "pointer",
                    }}
                >
                    Contact Me
                </button>
            </div>

            <FloatingSocialLinks isMobile={isMobile} />
        </div>
    );
}
