// ----- Project Card Component -----
function ProjectCard({ project, expandedId, handleExpand, cardStyle, cardHoverStyle }) {
  const [isHovered, setIsHovered] = useState(false);
  const isExpanded = project.id === expandedId;

  // Optional: GitHub link only for IoTBay
  const githubLink = project.id === 1 ? "https://github.com/jaeyunjks/IOTBay.git" : null;

  return (
    <div
      style={{
        ...cardStyle,
        ...(isHovered ? cardHoverStyle : {}),
        border: isExpanded ? "2px solid #25274d" : "1px solid #eee"
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={() => handleExpand(project.id)}
    >
      <img
        src={project.image}
        alt={project.title}
        style={{ width: "100%", borderRadius: 8, marginBottom: 14, height: 140, objectFit: "cover" }}
      />
      <h3 style={{ margin: "8px 0", fontSize: 22 }}>{project.title}</h3>
      <p style={{ color: "#555", marginBottom: 8 }}>{project.description}</p>
      <span style={{
        display: "inline-block",
        fontSize: 13,
        fontWeight: 600,
        background: "#25274d",
        color: "#fff",
        borderRadius: 13,
        padding: "3px 12px",
        marginBottom: 8
      }}>{project.tech}</span>

      {isExpanded && (
        <div style={{
          marginTop: 12,
          color: "#25274d",
          background: "#e6e8fa",
          borderRadius: 8,
          padding: 12,
          fontSize: 15,
          lineHeight: 1.5,
          animation: "fadeIn 0.25s ease-in-out"
        }}>
          {project.details}
          {githubLink && (
            <div style={{ marginTop: 10 }}>
              <a
                href={githubLink}
                target="_blank"
                rel="noreferrer"
                style={{
                  textDecoration: "none",
                  color: "#fff",
                  background: "#25274d",
                  padding: "6px 12px",
                  borderRadius: 6,
                  fontWeight: 600,
