function GlassCard({ children, style = {}, fade = true, ...rest }) {
    const [ref, show] = useScrollFadeIn();
    return (
        <div
            ref={ref}
            style={{
                background: "rgba(255,255,255,0.92)",
                borderRadius: "19px",
                boxShadow: "0 2px 19px #b1bee755",
                padding: "16px 17px",
                margin: "0 0 22px 0",
