import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import HomePage from "./pages/HomePage";
import AboutPage from "./pages/AboutPage";
import WorkPage from "./pages/WorkPage";
import PortfolioPage from "./pages/PortfolioPage";
import ContactPage from "./pages/ContactPage";
import InteractionDesignCase from "./pages/InteractionDesignCase";

// Mobile detection
function useIsMobile(breakpoint = 600) {
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < breakpoint);
  React.useEffect(() => {
    function handleResize() { setIsMobile(window.innerWidth < breakpoint); }
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [breakpoint]);
  return isMobile;
}

// Minimalist mobile navbar, desktop keeps original style
function Navbar() {
  const location = useLocation();
  const isMobile = useIsMobile();
  const navs = [
    { to: "/", label: "Home" },
    { to: "/about", label: "About" },
    { to: "/work", label: "Work" },
    { to: "/portfolio", label: "Portfolio" },
    { to: "/contact", label: "Contact" }
  ];
  return (
    <nav
      style={{
        width: "100vw",
        background: isMobile ? "#fff" : "rgba(249,252,252,.98)",
        boxShadow: isMobile ? "none" : "0 4px 23px #c7d8f733",
        padding: isMobile ? "5px 0 7px" : "17px 0 15px",
        display: "flex",
        justifyContent: isMobile ? "flex-start" : "center",
        alignItems: "center",
        position: "sticky",
        top: 0,
        borderRadius: isMobile ? 0 : 22,
        gap: isMobile ? 7 : 38,
        fontWeight: 700,
        fontSize: isMobile ? 13.8 : 19,
        zIndex: 99,
        overflowX: isMobile ? "auto" : "visible",
        margin: isMobile ? 0 : "0 auto",
        marginTop: isMobile ? 0 : "13px"
      }}
    >
      {navs.map(({ to, label }) => (
        <Link
          key={to}
          to={to}
          style={{
            color: location.pathname === to ? "#7B9ACC" : "#232751",
            textDecoration: "none",
            borderRadius: 11,
            background: isMobile
              ? (location.pathname === to ? "#EDF2FB" : "none")
              : (location.pathname === to ? "linear-gradient(89deg, #7B9ACC 75%, #A4B0E9 110%)" : "none"),
            padding: isMobile ? "7px 9vw" : "8px 15px",
            fontWeight: 800,
            boxShadow: isMobile ? "none" : (location.pathname === to ? "0 2px 9px #7b9acc38" : "none"),
            whiteSpace: "nowrap",
            fontSize: isMobile ? 13.8 : 19,
            border: "none",
            opacity: location.pathname === to ? 1 : 0.84,
            transition: "background .21s, color .19s",
            outline: "none"
          }}
        >
          {label}
        </Link>
      ))}
    </nav>
  );
}

export default function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/about" element={<AboutPage />} />
        <Route path="/work" element={<WorkPage />} />
        <Route path="/portfolio" element={<PortfolioPage />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path="/interaction-design-case" element={<InteractionDesignCase />} />
      </Routes>
    </Router>
  );
}
