import React, { useRef, useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

// OneDrive embed URL converter
function toOneDriveEmbedUrl(url) {
    const match = url.match(/1drv\.ms\/v\/[a-zA-Z0-9\-]+\/([a-zA-Z0-9\-_]+)\?/);
    if (!match) return url;
    const fileId = match[1];
    return `https://onedrive.live.com/embed?cid=3E8E6F7A169E86A5&resid=3E8E6F7A169E86A5%21${fileId}&authkey=!ALg-uT92_il9J07nQTI20g&em=2`;
}

const CASE_STUDY_STEPS = [
    {
        title: "Journal Entry",
        desc: "Initial reflections and thoughts regarding daily challenges incorporating design principles.",
        file: "/journal-entry.pdf",
        type: "pdf"
    },
    {
        title: "Affinity Diagram",
        desc: "Visual mapping of core pain points facing the persona.",
        file: "/affinity-diagram.png",
        type: "image"
    },
    {
        title: "Persona",
        desc: "Full persona outlining user background, needs, and motivations.",
        file: "/persona.png",
        type: "image"
    },
    {
        title: "Problem Scenario",
        desc: "Description of daily time management struggles, work/life balance and burnout.",
        content: `I sit down in my chair and relax for a bit while I take in what I need to do for the day...`
    },
    {
        title: "Future Use Scenario",
        desc: "Design solution improves wellbeing and productivity.",
        content: `I start everyday at the same time, not too early...`
    },
    {
        title: "Storyboard",
        desc: "User flow visual showing solution in context.",
        file: "/storyboard.png",
        type: "image"
    },
    {
        title: "Specified Goal",
        desc: "User goal for prototype evaluation: setup a personalized sit/stand schedule...",
        content: `To setup or import a personalized sit/stand schedule...`
    },
    {
        title: "Paper Prototype",
        desc: "Low fidelity paper prototype for initial usability testing.",
        images: [
            "/paperproto1.jpg",
            "/paperproto2.jpg",
            "/paperproto3.jpg"
        ],
        type: "slideshow"
    },
    {
        title: "Figma Mobile App Prototype",
        desc: "Interactive mobile app design, user testing in Figma.",
        videos: [
            {
                label: "Welcome Page",
                url: "https://1drv.ms/v/c/3e8e6f7a169e86a5/EaPw7_FgKalBvDz2PedO2zoB0Lg-uT92_il9J07nQTI20g?e=WH2o4L"
            },
            {
                label: "Main Feature",
                url: "https://1drv.ms/v/c/3e8e6f7a169e86a5/ESYWFNduxOpOpkABAiBJ5LQBYG9lpTywHcavnIrl8H7axg?e=t1Lmjj"
            }
        ],
        type: "onedrive-multivideo"
    },
    {
        title: "DIA Video",
        desc: "High fidelity demonstration and evaluation video prototypes.",
        videos: [
            {
                label: "DIA Demo",
                url: "https://1drv.ms/v/c/3e8e6f7a169e86a5/EdKk7T8ePfNInLWpkJxTCLgBkk6TPJ6tf1l48qsmM7_PVw?e=74reNU"
            }
        ],
        type: "onedrive-multivideo"
    }
];

const colors = [
