];

// PDF Modal
function PDFModal({ open, url, onClose }) {
    return open ? (
        <div style={{
            position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
            background: "rgba(60,80,120,0.19)", display: "flex", alignItems: "center",
            justifyContent: "center", zIndex: 2000
        }}>
            <motion.div
                initial={{ opacity: 0, scale: .88, y: 40 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.85, y: 60 }}
                transition={{ duration: 0.5 }}
                style={{
                    background: "#fff", borderRadius: 18, boxShadow: "0 4px 24px #0f143058",
                    padding: 22, width: "96vw", maxWidth: 620, maxHeight: "90vh",
                    display: "flex", flexDirection: "column"
                }}
            >
                <button onClick={onClose} style={{
                    marginLeft: "auto", marginBottom: 9, background: "#7B9ACC",
                    color: "#fff", borderRadius: 7, border: "none", fontWeight: 700,
                    padding: "7px 16px", cursor: "pointer"
                }}>
                    Close
                </button>
                <iframe src={url} title="PDF Viewer" style={{
                    width: "100%", height: "70vh", borderRadius: 10, border: "none", background: "#f8fbff"
                }} />
            </motion.div>
        </div>
    ) : null;
}

// Slideshow Component
function Slideshow({ images }) {
    const [index, setIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setIndex((i) => (i + 1) % images.length);
        }, 4000);
        return () => clearInterval(interval);
    }, [images.length]);

    return (
        <div style={{ position: "relative", width: "100%", maxWidth: 510, margin: "12px auto" }}>
            <motion.img
                key={index}
                src={images[index]}
                alt={`Slide ${index + 1}`}
                initial={{ opacity: 0, x: 100 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -100 }}
                transition={{ duration: 0.6 }}
                style={{
                    width: "100%", borderRadius: 12, boxShadow: "0 4px 20px #becae930"
                }}
            />
            <div style={{ display: "flex", justifyContent: "center", gap: 8, marginTop: 10 }}>
                {images.map((_, i) => (
                    <button
                        key={i}
                        onClick={() => setIndex(i)}
                        style={{
                            width: 10, height: 10, borderRadius: "50%",
                            background: i === index ? "#7B9ACC" : "#D0DEFA",
                            border: "none", cursor: "pointer",
                            transition: "background 0.3s"
                        }}
                    />
                ))}
            </div>
        </div>
    );
}

// iPhone Frame Component
function IPhoneFrame({ children }) {
    return (
        <div style={{
            position: "relative",
            width: 340,
            height: 680,
            margin: "20px auto",
            background: "#000",
            borderRadius: 40,
            padding: "60px 16px 60px",
            boxShadow: "0 20px 40px rgba(0,0,0,0.3)",
            overflow: "hidden"
        }}>
            <div style={{
                position: "absolute", top: 20, left: "50%", transform: "translateX(-50%)",
                width: 120, height: 24, background: "#000", borderRadius: 12
            }} />
            <div style={{
                width: "100%", height: "100%", background: "#fff", borderRadius: 30,
                overflow: "hidden", boxShadow: "inset 0 0 20px rgba(0,0,0,0.1)"
            }}>
                {children}
            </div>
        </div>
    );
}

