import React, { useState } from "react";

// ----- Project Data -----
const projects = [
  {
    id: 1,
    title: "IoTBay - IoT Devices Ordering Application",
    description: "A Java Web App for ordering and managing IoT devices online.",
    tech: "Java, JSP, JDBC, MVC, MySQL",
    details: `IoTBay is an online platform for customers to purchase IoT devices like sensors, actuators, and gateways. I led the shipment management module, implementing full CRUD operations and secure data handling. The app features user registration/login, device catalog, order/payment processing, and shipment tracking, all built with MVC architecture. Improved backend performance for faster order retrieval and collaborated in an Agile 3-person team using Git for version control. 
GitHub: https://github.com/jaeyunjks/IOTBay.git`,
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=500&h=300&fit=crop"
  },
  {
    id: 2,
    title: "TaskManager Pro",
    description: "A web-based application for managing tasks and priorities.",
    tech: "React, Node.js, MongoDB, Express",
    details: "Allows teams to collaboratively manage tasks, assign priorities, and track progress. Integrated with Slack and Google Calendar.",
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=500&h=300&fit=crop"
  },
  {
    id: 3,
    title: "IT Project Dashboard",
    description: "Dashboard for tracking IT projects, resources, and progress.",
    tech: "ASP.NET MVC, SQL Server, Bootstrap",
    details: "Visualize all project KPIs, manage team assignments, and export weekly reports. Role-based access control.",
    image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=500&h=300&fit=crop"
  },
  {
    id: 4,
    title: "Business Analytics Portal",
    description: "Analytics tool for visualizing KPIs and insights.",
    tech: "Python (Django), PostgreSQL, Chart.js",
    details: "Interactive dashboards for business users with exportable charts and real-time data updates.",
    image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=500&h=300&fit=crop"
  }
];

// ----- Skills Data -----
const skills = [
  "JavaScript", "Python", "C#", "SQL", "HTML & CSS",
  "React", "Node.js", "ASP.NET MVC", "Django",
  "Git", "Jira", "Figma", "Project Management"
];

// ----- Main App -----
export default function App() {
  const [expandedId, setExpandedId] = useState(null);

  const handleExpand = (projectId) => {
    setExpandedId(projectId === expandedId ? null : projectId);
  };

  const cardStyle = {
    background: "#fff",
    borderRadius: 10,
    boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
    padding: 20,
    cursor: "pointer",
    transition: "transform 0.2s, box-shadow 0.2s",
    display: "flex",
    flexDirection: "column"
  };

  const cardHoverStyle = {
    transform: "scale(1.04)",
    boxShadow: "0 8px 24px rgba(0,0,0,0.15)"
  };

  return (
    <div style={{ fontFamily: "Segoe UI,Arial,sans-serif", background: "#f7f7f7", minHeight: "100vh", margin: 0 }}>
      {/* Navigation */}
      <nav style={{
        background: "#25274d",
        color: "#fff",
        padding: "20px 0",
        display: "flex",
        justifyContent: "center",
        gap: "40px",
        fontWeight: "bold",
        letterSpacing: "1px"
      }}>
        {["About", "Projects", "Skills", "Contact"].map(section => (
          <a key={section} href={`#${section.toLowerCase()}`} style={{ color: "#fff", textDecoration: "none" }}>{section}</a>
        ))}
      </nav>

      {/* Main Content */}
      <main style={{ maxWidth: 900, margin: "40px auto", padding: "0 20px" }}>
        {/* About Section */}
        <section id="about" style={{ marginBottom: 60 }}>
          <h1 style={{ fontWeight: 800, fontSize: 42, marginBottom: 12 }}>Yafie Farabi</h1>
          <p style={{ fontSize: 18, color: "#444", lineHeight: 1.6 }}>
            Software Engineer & Business Analyst specializing in web development and MVC-based applications. Experienced in IT project management, database design, and building end-to-end solutions with a focus on user-friendly interfaces and scalable architectures.
          </p>
        </section>

        {/* Projects Section */}
        <section id="projects" style={{ marginBottom: 60 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Projects</h2>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))", gap: 24 }}>
            {projects.map(project => (
              <ProjectCard
                key={project.id}
                project={project}
                expandedId={expandedId}
                handleExpand={handleExpand}
                cardStyle={cardStyle}
                cardHoverStyle={cardHoverStyle}
              />
            ))}
          </div>
        </section>

        {/* Skills Section */}
        <section id="skills" style={{ marginBottom: 60 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Skills</h2>
          <div style={{ display: "flex", flexWrap: "wrap", gap: 10 }}>
            {skills.map(skill => (
              <span key={skill} style={{
                padding: "6px 14px",
                background: "#e2eafc",
                color: "#25274d",
                borderRadius: 12,
                fontWeight: 600,
                fontSize: 16
              }}>{skill}</span>
            ))}
          </div>
        </section>

        {/* Contact Section */}
        <section id="contact" style={{ marginBottom: 60 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Contact</h2>
          <div style={{ fontSize: 18, lineHeight: 1.8 }}>
            <div>üìß Email: <a href="mailto:yafie.farabi@email.com">yafie.farabi@email.com</a></div>
            <div>üíº LinkedIn: <a href="https://www.linkedin.com/in/yafiefarabi" target="_blank" rel="noreferrer">linkedin.com/in/yafiefarabi</a></div>
            <div>üêô GitHub: <a href="https://github.com/yafiefarabi" target="_blank" rel="noreferrer">github.com/yafiefarabi</a></div>
          </div>
        </section>
      </main>
    </div>
  );
}

// ----- Project Card Component -----
function ProjectCard({ project, expandedId, handleExpand, cardStyle, cardHoverStyle }) {
  const [isHovered, setIsHovered] = useState(false);
  const isExpanded = project.id === expandedId;

  // Optional: GitHub link only for IoTBay
  const githubLink = project.id === 1 ? "https://github.com/jaeyunjks/IOTBay.git" : null;

  return (
    <div
      style={{
        ...cardStyle,
        ...(isHovered ? cardHoverStyle : {}),
        border: isExpanded ? "2px solid #25274d" : "1px solid #eee"
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={() => handleExpand(project.id)}
    >
      <img
        src={project.image}
        alt={project.title}
        style={{ width: "100%", borderRadius: 8, marginBottom: 14, height: 140, objectFit: "cover" }}
      />
      <h3 style={{ margin: "8px 0", fontSize: 22 }}>{project.title}</h3>
      <p style={{ color: "#555", marginBottom: 8 }}>{project.description}</p>
      <span style={{
        display: "inline-block",
        fontSize: 13,
        fontWeight: 600,
        background: "#25274d",
        color: "#fff",
        borderRadius: 13,
        padding: "3px 12px",
        marginBottom: 8
      }}>{project.tech}</span>

      {isExpanded && (
        <div style={{
          marginTop: 12,
          color: "#25274d",
          background: "#e6e8fa",
          borderRadius: 8,
          padding: 12,
          fontSize: 15,
          lineHeight: 1.5,
          animation: "fadeIn 0.25s ease-in-out"
        }}>
          {project.details}
          {githubLink && (
            <div style={{ marginTop: 10 }}>
              <a
                href={githubLink}
                target="_blank"
                rel="noreferrer"
                style={{
                  textDecoration: "none",
                  color: "#fff",
                  background: "#25274d",
                  padding: "6px 12px",
                  borderRadius: 6,
                  fontWeight: 600,
                  display: "inline-block"
                }}
                onClick={(e) => e.stopPropagation()} // Prevent expanding/collapsing when clicking the link
              >
                View Code
              </a>
            </div>
          )}
        </div>
      )}

      <div style={{ marginTop: 10 }}>
        <button style={{
          background: "#25274d",
          color: "#fff",
          border: "none",
          borderRadius: 6,
          padding: "6px 12px",
          cursor: "pointer"
        }}>
          {isExpanded ? "Collapse" : "View Details"}
        </button>
      </div>
    </div>
  );
}
