import React, { useState, useEffect } from "react";

// CHANGE: ganti data di bawah dengan data/project punyamu
const projects = [
  {
    id: 1,
    title: "IoTBay - IoT Devices Ordering",
    tech: ["Java", "JSP", "MVC", "MySQL"],
    summary: "Web App for IoT device catalog, order, shipment; team of 6; full CRUD shipment module, secure backend.",
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=700&h=380&fit=crop",
    details: "IoTBay is an online platform for ordering sensors, actuators, and gateways. I led shipment management module, handled CRUD ops, collaborated in Agile team, implemented scalable architecture and optimized query performance.",
    github: "https://github.com/jaeyunjks/IOTBay.git"
  },
  {
    id: 2,
    title: "Interaction Design Project",
    tech: ["Figma", "UX Research", "Web"],
    summary: "Web-based project management/UX case study. Detail process: persona, prototype, usability test, video summary.",
    image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=700&h=380&fit=crop",
    details: "Designed and tested collaborative task manager following HCI principles–includes process documentation from initial research, persona, storyboard, prototype, usability study, to DIA video.",
    designProcess: [
      { step: "Journal Entry", file: "/journal-entry.pdf" },
      { step: "Persona", file: "/persona.pdf" },
      { step: "Storyboard", file: "/storyboard.png" },
      { step: "Paper Prototype", file: "/paperproto.pdf" },
      { step: "DIA Video", file: "/dia-fid.mp4" }
    ]
  }
];

const calmBg = "#F9FAFC"; // Soft white
const accent = "#7B9ACC"; // Calm blue
const navText = "#232751";
const cardBg = "#EDF2FB";

// util for animated hero
function useAnimateText(text, speed = 35) {
  const [display, setDisplay] = useState("");
  useEffect(() => {
    let i = 0;
    setDisplay("");
    if (!text) return;
    const timer = setInterval(() => {
      setDisplay(text.slice(0, i + 1));
      i++;
      if (i === text.length) clearInterval(timer);
    }, speed);
    return () => clearInterval(timer);
  }, [text, speed]);
  return display;
}

export default function App() {
  const heroText = "Hi, I'm Yafie, a Software Engineer & UX Enthusiast.";
  const heroDisplay = useAnimateText(heroText, 30);

  const [expanded, setExpanded] = useState(null);

  return (
    <div style={{ minHeight: "100vh", background: calmBg, fontFamily: "Nunito,Arial,sans-serif", color: navText }}>
      {/* Navbar */}
      <nav style={{
        width: "100%", background: "#fff", boxShadow: "0 2px 13px #d2ddec22",
        padding: "20px 0 15px", display: "flex", justifyContent: "center",
        position: "sticky", top: 0, gap: 38, fontWeight: 700, fontSize: 19, zIndex: 99, letterSpacing: 0.2
      }}>
        {["Home", "Projects", "Contact"].map(link =>
          <a key={link} href={`#${link.toLowerCase()}`}
            style={{
              color: navText, textDecoration: "none", margin: "0 6px", borderRadius: 19,
              padding: "8px 14px", opacity: 0.82
            }}>{link}</a>
        )}
      </nav>

      <main style={{ maxWidth: 900, margin: "48px auto", padding: "0 28px" }}>
        {/* Hero */}
        <section id="home" style={{ minHeight: 290, display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "flex-start", marginBottom: 50 }}>
          <h1 style={{ fontWeight: 800, fontSize: 44, marginBottom: 16, color: accent, letterSpacing: -2, minHeight: 54 }}>
            {heroDisplay}
          </h1>
          <div style={{ fontSize: 18, marginBottom: 14, color: navText, opacity: .75 }}>
            IT Undergraduate, Web & Data | Open for 2025 Internship <br />
            McGill-style, but made by Yafie.
          </div>
          <div style={{ display: "flex", gap: 12 }}>
            <a href="mailto:yafie.farabi@email.com" style={{ color: "#fff", background: accent, fontWeight: 700, borderRadius: 14, padding: "8px 22px", textDecoration: "none" }}>Contact</a>
            <a href="https://linkedin.com/in/yafiefarabi" style={{ color: accent, fontWeight: 600, textDecoration: "underline" }} target="_blank" rel="noreferrer">LinkedIn</a>
            <a href="https://github.com/yafiefarabi" style={{ color: accent, fontWeight: 600, textDecoration: "underline" }} target="_blank" rel="noreferrer">GitHub</a>
          </div>
        </section>

        {/* Projects Section */}
        <section id="projects" style={{ marginBottom: 72 }}>
          <h2 style={{ fontWeight: 800, fontSize: 30, marginBottom: 27, color: navText }}>Projects</h2>
          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 32 }}>
            {projects.map(proj => (
              <div
                key={proj.id}
                style={{
                  background: cardBg,
                  borderRadius: 18,
                  boxShadow: "0 2px 18px #c0c7ef29",
                  padding: 21,
                  cursor: "pointer",
                  transition: "box-shadow .25s, border .18s",
                  border: expanded === proj.id ? `2.2px solid ${accent}` : "1.2px solid #e3e8f2"
                }}
                onClick={() => setExpanded(expanded === proj.id ? null : proj.id)}
              >
                <img src={proj.image} alt={proj.title} style={{ width: "100%", borderRadius: 11, maxHeight: 155, marginBottom: 11 }} />
                <div style={{ fontWeight: 700, fontSize: 22, margin: "10px 0", color: navText }}>{proj.title}</div>
                <div style={{ color: accent, fontWeight: 600, fontSize: 15, letterSpacing: 1, marginBottom: 7 }}>
                  {proj.tech && proj.tech.join(" · ")}
                </div>
                <div style={{ fontSize: 15, opacity: .78, marginBottom: 8 }}>{proj.summary}</div>
                <div style={{ fontSize: 14, color: "#798", marginBottom: 10 }}><i>Click for details</i></div>
                {/* Details expanded */}
                {expanded === proj.id && (
                  <div style={{
                    marginTop: 7, borderRadius: 10, background: "#fafcff",
                    color: navText, fontSize: 15, lineHeight: 1.6, padding: "14px 12px"
                  }}>
                    <div style={{ marginBottom: 10 }}>{proj.details}</div>
                    {proj.github &&
                      <a href={proj.github} target="_blank" rel="noreferrer"
                        style={{
                          background: accent, color: "#fff", borderRadius: 9,
                          textDecoration: "none", fontWeight: 700,
                          padding: "6px 18px", marginRight: 14
                        }}>View Code</a>}
                    {/* If ada designProcess */}
                    {proj.designProcess &&
                      <>
                        <div style={{ fontWeight: 700, marginTop: 16, marginBottom: 9, fontSize: 17 }}>Design Case Study & Files:</div>
                        <ul style={{ paddingLeft: 19, margin: 0 }}>
                          {proj.designProcess.map((step, i) => (
                            <li key={step.step} style={{ marginBottom: 8 }}>
                              <span style={{ fontWeight: 600 }}>{step.step}:</span>
                              {step.file && (
                                step.file.endsWith(".png") ?
                                  <img src={step.file} alt={step.step}
                                    style={{ display: "block", width: "94%", maxWidth: 230, borderRadius: 7, margin: "9px 0 2px" }} /> :
                                  step.file.endsWith(".pdf") ?
                                    <a href={step.file} target="_blank" rel="noreferrer"
                                      style={{
                                        background: accent, color: "#fff", borderRadius: 6, padding: "4px 12px",
                                        textDecoration: "none", fontWeight: 600, marginLeft: 10
                                      }}>View PDF</a> :
                                    step.file.endsWith(".mp4") ?
                                      <video src={step.file} controls style={{ width: "93%", maxWidth: 270, borderRadius: 8, marginLeft: 6, marginTop: 8 }} /> : null
                              )}
                            </li>
                          ))}
                        </ul>
                      </>
                    }
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Contact Section */}
        <section id="contact" style={{ margin: "6vh 0 2vh", color: "#445", fontSize: 18, fontWeight: 600 }}>
          Let's connect: <a href="mailto:yafie.farabi@email.com" style={{ color: accent }}>Email</a>
          {" · "}
          <a href="https://linkedin.com/in/yafiefarabi" style={{ color: accent }}>LinkedIn</a>
          {" · "}
          <a href="https://github.com/yafiefarabi" style={{ color: accent }}>GitHub</a>
        </section>
      </main>
    </div>
  );
}
