// src/components/ParticlesBackground.js
import { useEffect, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react";
import { loadSlim } from "@tsparticles/slim";
import { useTheme } from "../context/ThemeContext"; // import ini supaya bisa ikut tema

export default function ParticlesBackground() {
    const { theme } = useTheme(); // ambil tema dari context
    const [init, setInit] = useState(false);

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
        }).then(() => {
            setInit(true);
        });
    }, []);

    const particlesLoaded = (container) => {
        // console.log("Particles loaded:", container); // debug kalau perlu
    };

    // Warna partikel & background sesuai tema
    const isDark = theme === "dark";

    return init ? (
        <Particles
            id="tsparticles"
            particlesLoaded={particlesLoaded}
            style={{
                position: "fixed",
                inset: 0,
                width: "100%",
                height: "100vh",
                zIndex: -1,
                pointerEvents: "none",
            }}
            options={{
                fullScreen: { enable: true, zIndex: -1 },
                fpsLimit: 90, // turunin sedikit biar smooth di semua device
                background: {
                    color: {
                        value: "transparent", // penting! biar ga nutupin, ikut overlay App.js
                    },
                },
                particles: {
                    number: {
                        value: isDark ? 80 : 60, // lebih sedikit di light biar ga crowded
                        density: { enable: true, area: 800 },
                    },
                    color: {
                        value: isDark
                            ? ["#e2e8f0", "#c9d1d9", "#a5d6ff", "#ffffff"] // putih + biru cerah di dark
                            : ["#7b9acc", "#a5b4fc", "#c7d2fe", "#eff6ff"], // biru soft pastel di light
                    },
                    shape: {
                        type: "circle",
                    },
                    opacity: {
                        value: isDark ? 0.6 : 0.4,
                        random: true,
                    },
                    size: {
                        value: { min: 1, max: 5 },
                    },
                    move: {
                        enable: true,
                        speed: isDark ? 1.8 : 1.2, // sedikit lebih cepat di dark biar hidup
                        direction: "none",
                        random: true,
                        straight: false,
                        outModes: "out",
                    },
                    links: {
                        enable: true,
                        distance: 180,
                        color: isDark ? "#a5d6ff" : "#7b9acc",
                        opacity: isDark ? 0.5 : 0.3,
                        width: 1,
                    },
                },
                interactivity: {
                    events: {
                        onHover: { enable: true, mode: "grab" }, // grab biar interaktif (tarik garis)
                        onClick: { enable: true, mode: "push" },
                        resize: true,
                    },
                    modes: {
                        grab: { distance: 200, links: { opacity: 0.7 } },
                        push: { quantity: 4 },
                    },
                },
                detectRetina: true,
            }}
        />
    ) : null;
}
