// src/components/ParticlesBackground.js
import { useEffect, useState } from "react";
import Particles, { initParticlesEngine } from "@tsparticles/react"; // <-- tambah { initParticlesEngine } di sini!
import { loadSlim } from "@tsparticles/slim";

export default function ParticlesBackground() {
    const [init, setInit] = useState(false);

    useEffect(() => {
        initParticlesEngine(async (engine) => {
            await loadSlim(engine);
            // Kalau mau full features (lebih berat): await loadFull(engine); tapi harus install "tsparticles" atau "@tsparticles/full"
        }).then(() => {
            setInit(true);
        });
    }, []);

    const particlesLoaded = (container) => {
        // console.log("Particles loaded:", container); // optional debug
    };

    return init ? (
        <Particles
            id="tsparticles"
            particlesLoaded={particlesLoaded}
            options={{
                fullScreen: { enable: true, zIndex: -1 }, // tambah ini biar full screen pasti
                background: {
                    color: {
                        value: "#0d1117", // gelap cocok dark mode, bisa ganti nanti pas light mode
                    },
                },
                fpsLimit: 120,
                interactivity: {
                    events: {
                        onClick: {
                            enable: true,
                            mode: "push",
                        },
                        onHover: {
                            enable: true,
                            mode: "repulse",
                        },
                        resize: true,
                    },
                    modes: {
                        push: {
                            quantity: 4,
                        },
                        repulse: {
                            distance: 120,
                            duration: 0.4,
                        },
                    },
                },
                particles: {
                    color: {
                        value: ["#ffffff", "#e0f7fa", "#b3e5fc"],
                    },
                    links: {
                        color: "#ffffff",
                        distance: 150,
                        enable: true,
                        opacity: 0.4,
                        width: 1,
                    },
                    move: {
                        direction: "none",
                        enable: true,
                        outModes: {
                            default: "out",
                        },
                        random: true,
                        speed: 1.2,
                        straight: false,
                    },
                    number: {
                        density: {
                            enable: true,
                            area: 800,
                        },
                        value: 70, // kurangin ke 40-50 kalau terlalu berat di mobile
                    },
                    opacity: {
                        value: 0.5,
                        random: true,
                    },
                    shape: {
                        type: "circle",
                    },
                    size: {
                        value: { min: 1, max: 4 },
                    },
                },
                detectRetina: true,
            }}
        />
    ) : null;
}
