/* src/pages/WorkPage.js */
import React from "react";

/* -------------------------------------------------
   Mobile detection hook (sama)
------------------------------------------------- */
function useIsMobile(breakpoint = 768) {  // naikin jadi 768 biar tablet juga aman
    const [isMobile, setIsMobile] = React.useState(
        typeof window !== "undefined" && window.innerWidth < breakpoint
    );

    React.useEffect(() => {
        const onResize = () => setIsMobile(window.innerWidth < breakpoint);
        window.addEventListener("resize", onResize);
        return () => window.removeEventListener("resize", onResize);
    }, [breakpoint]);

    return isMobile;
}

/* DATA WORKS (tetap sama kayak punya kamu) */
const WORKS = [ /* ... isi 2 kerjaan kamu ... */];

/* -------------------------------------------------
   Skill Pill – VERSI SUPER FIXED
------------------------------------------------- */
function SkillPill({ skill, isMobile }) {
    const [show, setShow] = React.useState(false);
    const [hovered, setHovered] = React.useState(false);

    // Close popup kalau klik di luar (mobile only)
    React.useEffect(() => {
        if (!isMobile || !show) return;
        const close = () => setShow(false);
        document.addEventListener("click", close);
        return () => document.removeEventListener("click", close);
    }, [show, isMobile]);

    return (
        <div style={{ position: "relative", display: "inline-block" }}>
            {/* Pill */}
            <span
                className="skill-pill"
                onMouseEnter={() => !isMobile && setHovered(true)}
                onMouseLeave={() => !isMobile && setHovered(false)}
                onClick={(e) => {
                    e.stopPropagation();
                    if (isMobile) setShow(v => !v);
                }}
                style={{
                    background: (hovered || (isMobile && show)) ? "#A7C1F2" : "#D0DEFA",
                    color: "#232751",
                    borderRadius: 12,
                    padding: isMobile ? "8px 14px" : "7px 18px",
                    fontWeight: 700,
                    fontSize: isMobile ? 13 : 15,
                    cursor: "pointer",
                    display: "inline-block",
                    transition: "all 0.25s ease",
                    transform: (hovered || (isMobile && show)) ? "scale(1.07)" : "scale(1)",
                    boxShadow: (hovered || (isMobile && show))
                        ? "0 6px 16px rgba(0,0,0,0.18)"
                        : "0 2px 6px rgba(0,0,0,0.1)",
                    userSelect: "none",
                    whiteSpace: "nowrap",
                }}
            >
                {skill.name}
            </span>

            {/* Popup – SEKARANG PAKAI PORTAL BIAR AMAN */}
            {(isMobile ? show : hovered) && (
                <div
                    style={{
                        position: "fixed",              // fixed biar nggak ganggu layout
                        top: "50%",
                        left: "50%",
                        transform: "translate(-50%, -50%)",
                        width: isMobile ? "88vw" : "420px",
                        maxWidth: 420,
                        maxHeight: "85vh",
                        overflowY: "auto",
                        background: "rgba(255,255,255,0.97)",
                        backdropFilter: "blur(20px)",
                        borderRadius: 20,
                        padding: "20px 22px",
                        boxShadow: "0 20px 50px rgba(0,0,0,0.25)",
                        border: "1px solid rgba(255,255,255,0.4)",
                        zIndex: 9999,
                        fontSize: 14.5,
                        lineHeight: 1.65,
                    }}
                >
                    {[
                        { title: "What I did", text: skill.what },
                        { title: "Achievements", text: skill.achieve },
                        { title: "Transferable IT", text: skill.it },
                    ].map((sec, i) => (
                        <div key={i} style={{ marginTop: i === 0 ? 0 : 18 }}>
                            <div style={{ color: "#7B9ACC", fontWeight: 700, fontSize: 14.5, marginBottom: 6 }}>
                                {sec.title}
                            </div>
                            <div style={{ color: "#1a1a2e" }}>{sec.text}</div>
                        </div>
                    ))}

                    {isMobile && (
                        <button
                            onClick={() => setShow(false)}
                            style={{
                                marginTop: 24,
                                width: "100%",
                                padding: "12px",
                                background: "#7B9ACC",
                                color: "white",
                                border: "none",
                                borderRadius: 12,
                                fontWeight: 700,
                                fontSize: 15,
                                cursor: "pointer"
                            }}
                        >
                            Close
                        </button>
                    )}
                </div>
            )}

            {/* Overlay hitam tipis di mobile supaya lebih dramatis */}
            {isMobile && show && (
                <div
                    style={{
                        position: "fixed",
                        inset: 0,
                        background: "rgba(0,0,0,0.5)",
                        zIndex: 9998,
                    }}
                    onClick={() => setShow(false)}
                />
            )}
        </div>
    );
}

/* -------------------------------------------------
   Work Card – LAYOUT LEBIH RAPI
------------------------------------------------- */
function WorkCard({ work, isMobile }) {
    const [visible, setVisible] = React.useState(false);

    React.useEffect(() => {
        const t = setTimeout(() => setVisible(true), 100);
        return () => clearTimeout(t);
    }, []);

    return (
        <div
            style={{
                background: "#ffffff",
                border: "1px solid #e2e8f0",
                borderRadius: 20,
                padding: isMobile ? "20px" : "32px",
                boxShadow: visible ? "0 10px 30px rgba(0,0,0,0.08)" : "none",
                marginBottom: isMobile ? 20 : 28,
                transition: "all 0.4s ease",
                transform: visible ? "translateY(0)" : "translateY(20px)",
                opacity: visible ? 1 : 0,
            }}
        >
            <h3 style={{ margin: 0, fontSize: isMobile ? 18 : 26, fontWeight: 800, color: "#1e293b" }}>
                {work.role}
            </h3>
            <p style={{ margin: "8px 0 4px", fontSize: isMobile ? 15 : 20, color: "#475569" }}>
                <strong>{work.company}</strong> — <span style={{ color: "#7B9ACC" }}>{work.period}</span>
            </p>
            <p style={{ margin: "0 0 12px", fontSize: isMobile ? 13 : 16, color: "#64748b" }}>
                {work.location}
            </p>

            <p style={{ margin: "16px 0 20px", fontSize: isMobile ? 14.5 : 17, lineHeight: 1.7, color: "#334155" }}>
                {work.summary}
            </p>

            {/* Skill Pills – grid biar rapi banget */}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: isMobile ? "repeat(2, 1fr)" : "repeat(auto-fill, minmax(180px, 1fr))",
                    gap: isMobile ? "10px" : "12px",
                    marginTop: 8,
                }}
            >
                {work.stack.map((s) => (
                    <SkillPill key={s.name} skill={s} isMobile={isMobile} />
                ))}
            </div>
        </div>
    );
}

/* -------------------------------------------------
   Page
------------------------------------------------- */
export default function WorkPage() {
    const isMobile = useIsMobile();

    return (
        <div
            style={{
                maxWidth: 1000,
                margin: "40px auto",
                padding: isMobile ? "0 5vw" : "0 20px",
                fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
            }}
        >
            <h1
                style={{
                    fontSize: isMobile ? 32 : 48,
                    fontWeight: 900,
                    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                    WebkitBackgroundClip: "text",
                    WebkitTextFillColor: "transparent",
                    textAlign: isMobile ? "center" : "left",
                    marginBottom: 50,
                }}
            >
                Work Experience
            </h1>

            <div style={{ display: "flex", flexDirection: "column", gap: isMobile ? 32 : 40 }}>
                {WORKS.map((work, idx) => (
                    <WorkCard
                        key={idx}
                        work={work}
                        isMobile={isMobile}
                    />
                ))}
            </div>
        </div>
    );
}
