import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Shield, AlertTriangle, Target, CheckCircle, ArrowRight, FileCheck, Zap, Eye } from 'lucide-react';

const theme = {
    primary: '#5DADE2',
    light: '#A7C7E7',
    bg: '#F8FBFF',
    text: '#1A365D',
    white: '#FFFFFF',
    success: '#10B981',
    danger: '#EF4444',
    warning: '#F59E0B',
};

const fadeIn = { hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } };

export default function StaticTestingShowcase() {
    const [activeView, setActiveView] = useState('overview');

    return (
        <div style={{
            minHeight: '100vh',
            background: theme.bg,
            fontFamily: "'Inter', sans-serif",
            color: theme.text,
        }}>
            {/* Hero */}
            <section style={{ padding: '80px 20px', textAlign: 'center', background: `linear-gradient(135deg, ${theme.light} 0%, ${theme.primary} 100%)`, color: 'white' }}>
                <motion.h1
                    initial={{ opacity: 0, y: -30 }}
                    animate={{ opacity: 1, y: 0 }}
                    style={{ fontSize: '3rem', fontWeight: 800, margin: 0 }}
                >
                    Static Testing & Risk Analysis
                </motion.h1>
                <motion.p
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                    style={{ fontSize: '1.3rem', margin: '16px 0 32px', maxWidth: '700px', marginLeft: 'auto', marginRight: 'auto' }}
                >
                    Identified <strong>20 requirement defects</strong> and <strong>20 high-impact risks</strong> — before a single line of code.
                </motion.p>

                <motion.div
                    initial={{ scale: 0.8 }}
                    animate={{ scale: 1 }}
                    transition={{ delay: 0.4 }}
                    style={{ display: 'flex', justifyContent: 'center', gap: '30px', flexWrap: 'wrap' }}
                >
                    <ImpactCard icon={<Shield />} value="40" label="Issues Found" />
                    <ImpactCard icon={<Target />} value="RPN 20" label="Top Risk" />
                    <ImpactCard icon={<Zap />} value="10x" label="Cost Saved" />
                </motion.div>
            </section>

            {/* Navigation */}
            <div style={{ display: 'flex', justifyContent: 'center', gap: '12px', padding: '30px 20px', flexWrap: 'wrap' }}>
                {['overview', 'defects', 'risks', 'impact'].map((tab) => (
                    <button
                        key={tab}
                        onClick={() => setActiveView(tab)}
                        style={{
                            padding: '12px 28px',
                            border: 'none',
                            borderRadius: '50px',
                            background: activeView === tab ? theme.primary : theme.white,
                            color: activeView === tab ? 'white' : theme.text,
                            fontWeight: 600,
                            cursor: 'pointer',
                            boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
                            transition: '0.3s',
                        }}
                    >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>

            {/* Content */}
            <div style={{ maxWidth: '1100px', margin: '0 auto 80px', padding: '0 20px' }}>
                {activeView === 'overview' && <Overview />}
                {activeView === 'defects' && <Defects />}
                {activeView === 'risks' && <Risks />}
                {activeView === 'impact' && <Impact />}
            </div>

            {/* CTA Footer */}
            <motion.footer
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                style={{
                    background: theme.primary,
                    color: 'white',
                    textAlign: 'center',
                    padding: '40px 20px',
                }}
            >
                <h3 style={{ margin: '0 0 16px', fontSize: '1.5rem' }}>Ready to build defect-free systems?</h3>
                <p style={{ margin: '0 0 24px', opacity: 0.9 }}>Let’s discuss how early testing drives quality and saves millions.</p>
                <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    style={{
                        background: 'white',
                        color: theme.primary,
                        border: 'none',
                        padding: '14px 32px',
                        borderRadius: '50px',
                        fontWeight: 600,
                        fontSize: '1.1rem',
                        cursor: 'pointer',
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '8px',
                    }}
                >
                    View Full Report <ArrowRight size={20} />
                </motion.button>
            </motion.footer>
        </div>
    );
}

// Components
function ImpactCard({ icon, value, label }) {
    return (
        <motion.div
            whileHover={{ y: -8 }}
            style={{
                background: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                padding: '20px 30px',
                borderRadius: '16px',
                minWidth: '160px',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '2.5rem', marginBottom: '8px' }}>{icon}</div>
            <div style={{ fontSize: '2rem', fontWeight: 800 }}>{value}</div>
            <div style={{ fontSize: '0.9rem', opacity: 0.9 }}>{label}</div>
        </motion.div>
    );
}

function Overview() {
    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Early Detection = Massive ROI</h2>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '24px', marginTop: '30px' }}>
                <FeatureCard
                    icon={<FileCheck />}
                    title="Static Requirements Review"
                    desc="Analyzed two real-world specs. Found 20 critical defects in security, performance, and usability."
                />
                <FeatureCard
                    icon={<AlertTriangle />}
                    title="FMEA Risk Prioritization"
                    desc="Mapped 20 failure modes. Prioritized using Severity × Priority = RPN. Top risk: RPN 20."
                />
                <FeatureCard
                    icon={<Eye />}
                    title="100% Traceability"
                    desc="Every issue linked to exact document location. Zero ambiguity. Actionable for developers."
                />
            </div>
        </motion.div>
    );
}

function Defects() {
    const samples = [
        { id: 'ST001', cat: 'Security', issue: 'Password rules undefined', tags: ['Incomplete', 'Ambigious'] },
        { id: 'ST013', cat: 'Process', issue: 'Advisor approval conflict', tags: ['Inconsistent'] },
        { id: 'ST006', cat: 'UX', issue: 'Color blindness not specified', tags: ['Untestable'] },
    ];

    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Sample Defects Found</h2>
            <div style={{ overflowX: 'auto', marginTop: '20px' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                        <tr style={{ background: theme.primary, color: 'white' }}>
                            <th style={th}>ID</th>
                            <th style={th}>Category</th>
                            <th style={th}>Issue</th>
                            <th style={th}>Tags</th>
                        </tr>
                    </thead>
                    <tbody>
                        {samples.map((d, i) => (
                            <motion.tr key={d.id} initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: i * 0.1 }}>
                                <td style={td}><strong>{d.id}</strong></td>
                                <td style={td}><Badge text={d.cat} /></td>
                                <td style={td}>{d.issue}</td>
                                <td style={td}>{d.tags.map(t => <Badge key={t} text={t} color={theme.danger} />)}</td>
                            </motion.tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <p style={{ marginTop: '20px', color: '#64748B', fontStyle: 'italic' }}>
                +17 more in full report. All traceable. All fixable before coding.
            </p>
        </motion.div>
    );
}

function Risks() {
    const samples = [
        { id: 'RS018', cat: 'Localization', fail: 'No non-English support', rpn: 20 },
        { id: 'RS003', cat: 'UI', fail: 'Scrolling breaks on mobile', rpn: 12 },
        { id: 'RS001', cat: 'Security', fail: 'Email delay → user deleted', rpn: 6 },
    ];

    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Top Risks (RPN Prioritized)</h2>
            <div style={{ display: 'grid', gap: '16px', marginTop: '20px' }}>
                {samples.map((r, i) => (
                    <motion.div
                        key={r.id}
                        initial={{ x: -30 }}
                        animate={{ x: 0 }}
                        transition={{ delay: i * 0.1 }}
                        style={{
                            background: theme.white,
                            padding: '20px',
                            borderRadius: '12px',
                            boxShadow: '0 4px 15px rgba(0,0,0,0.05)',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                        }}
                    >
                        <div>
                            <strong>{r.id}</strong> — <Badge text={r.cat} /> — {r.fail}
                        </div>
                        <div style={{
                            background: r.rpn > 15 ? theme.danger : r.rpn > 10 ? theme.warning : theme.success,
                            color: 'white',
                            padding: '8px 16px',
                            borderRadius: '50px',
                            fontWeight: 'bold',
                        }}>
                            RPN {r.rpn}
                        </div>
                    </motion.div>
                ))}
            </div>
        </motion.div>
    );
}

function Impact() {
    return (
        <motion.div variants={fadeIn} initial="hidden" animate="visible" style={sectionStyle}>
            <h2 style={titleStyle}>Business Impact</h2>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))', gap: '24px', marginTop: '30px' }}>
                <ImpactBox
                    title="Cost Avoidance"
                    value="10x"
                    desc="Fixing in requirements vs. production"
                />
                <ImpactBox
                    title="Risk Reduction"
                    value="88%"
                    desc="Critical paths covered"
                />
                <ImpactBox
                    title="Time Saved"
                    value="60%"
                    desc="No rework in dev/test"
                />
            </div>
        </motion.div>
    );
}

// Helpers
function FeatureCard({ icon, title, desc }) {
    return (
        <motion.div
            whileHover={{ y: -8 }}
            style={{
                background: theme.white,
                padding: '24px',
                borderRadius: '16px',
                boxShadow: '0 8px 25px rgba(0,0,0,0.08)',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '2.5rem', color: theme.primary, marginBottom: '16px' }}>{icon}</div>
            <h3 style={{ margin: '0 0 12px', fontSize: '1.2rem' }}>{title}</h3>
            <p style={{ margin: 0, color: '#64748B', fontSize: '0.95rem' }}>{desc}</p>
        </motion.div>
    );
}

function Badge({ text, color = theme.primary }) {
    return (
        <span style={{
            background: color,
            color: 'white',
            padding: '4px 12px',
            borderRadius: '50px',
            fontSize: '0.75rem',
            fontWeight: 600,
            display: 'inline-block',
            margin: '2px',
        }}>
            {text}
        </span>
    );
}

function ImpactBox({ title, value, desc }) {
    return (
        <motion.div
            whileHover={{ scale: 1.03 }}
            style={{
                background: `linear-gradient(135deg, ${theme.light} 0%, ${theme.primary} 100%)`,
                color: 'white',
                padding: '30px',
                borderRadius: '16px',
                textAlign: 'center',
            }}
        >
            <div style={{ fontSize: '3rem', fontWeight: 800, marginBottom: '8px' }}>{value}</div>
            <h4 style={{ margin: '0 0 8px' }}>{title}</h4>
            <p style={{ margin: 0, fontSize: '0.9rem', opacity: 0.9 }}>{desc}</p>
        </motion.div>
    );
}

// Styles
const sectionStyle = { background: theme.white, padding: '40px', borderRadius: '20px', boxShadow: '0 10px 40px rgba(0,0,0,0.08)' };
const titleStyle = { fontSize: '2rem', fontWeight: 700, textAlign: 'center', margin: '0 0 10px', color: theme.text };
const th = { padding: '16px', textAlign: 'left', fontWeight: 600 };
const td = { padding: '16px', borderBottom: `1px solid #E2E8F0` };
