import React, { useRef, useState, useEffect } from "react";
import emailjs from "@emailjs/browser";

/* ────────────────────── RESPONSIVE HOOK ────────────────────── */
function useIsMobile(breakpoint = 600) {
    const [isMobile, setIsMobile] = useState(window.innerWidth < breakpoint);
    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < breakpoint);
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, [breakpoint]);
    return isMobile;
}

/* ────────────────────── MAIN COMPONENT ────────────────────── */
export default function ContactPage() {
    const form = useRef();
    const isMobile = useIsMobile();
    const today = new Date().toLocaleString();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm(
                "service_jkgvb2e",
                "template_hm943z9",
                form.current,
                { publicKey: "cfBYxuTPnUhXt9G1j" }
            )
            .then(
                () => {
                    alert("Message sent successfully!");
                    form.current.reset();
                },
                (error) => {
                    alert("Failed to send message.\n" + error.text);
                }
            );
    };

    return (
        <div
            style={{
                maxWidth: isMobile ? "98vw" : 960,
                margin: isMobile ? "30px auto 10px" : "80px auto 32px",
                color: "#232751",
                padding: isMobile ? "0 4vw" : "0 15px",
                fontFamily: "'Segoe UI', Arial, sans-serif",
            }}
        >
            {/* ────── Header ────── */}
            <h1
                style={{
                    fontWeight: 900,
                    fontSize: isMobile ? 28 : 38,
                    color: "#7B9ACC",
                    marginBottom: isMobile ? 15 : 24,
                    textAlign: "center",
                }}
            >
                Contact Me
            </h1>

            {/* ────── Intro text ────── */}
            <p
                style={{
                    fontSize: isMobile ? 15 : 18,
                    marginBottom: isMobile ? 18 : 23,
                    textAlign: "center",
                    lineHeight: 1.6,
                }}
            >
                Thank you for your interest in getting in touch!<br />
                I value open communication and welcome any inquiries, feedback, or
                collaboration opportunities.<br /><br />
                Feel free to reach out using the form below or via:
            </p>

            {/* ────── Social links ────── */}
            <div
                style={{
                    fontSize: isMobile ? 15 : 20,
                    fontWeight: 700,
                    margin: isMobile ? 12 : 20,
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    alignItems: "center",
                    justifyContent: "center",
                    gap: isMobile ? 8 : 28,
                }}
            >
                <a
                    href="https://www.linkedin.com/in/yafiefarabi0710/"
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                        color: "#7B9ACC",
                        textDecoration: "none",
                    }}
                >
                    LinkedIn
                </a>
                <a
                    href="mailto:yaafiiee.10@gmail.com"
                    style={{
                        color: "#7B9ACC",
                        textDecoration: "none",
                    }}
                >
                    yaafiiee.10@gmail.com
                </a>
            </div>

            {/* ────── Layout: Avatar + Form ────── */}
            <div
                style={{
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    alignItems: "flex-start",
                    justifyContent: "center",
                    gap: isMobile ? 20 : 40,
                    marginTop: isMobile ? 30 : 50,
                }}
            >
                {/* ────── Picture placeholder ────── */}
                <div
                    style={{
                        flexShrink: 0,
                        width: isMobile ? 120 : 160,
                        height: isMobile ? 120 : 160,
                        borderRadius: "50%",
                        background: "#E6EBF5",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        overflow: "hidden",
                        boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
                    }}
                >
                    {/* Replace the src with your real photo later */}
                    <img
                        src="https://via.placeholder.com/160?text=Your+Photo"
                        alt="Your portrait"
                        style={{
                            width: "100%",
                            height: "100%",
                            objectFit: "cover",
                        }}
                    />
                </div>

                {/* ────── Contact form ────── */}
                <form
                    ref={form}
                    onSubmit={sendEmail}
                    style={{
                        flex: 1,
                        maxWidth: isMobile ? "100%" : 420,
                        background: "#F9FAFC",
                        borderRadius: 14,
                        padding: isMobile ? "16px 6vw" : "32px",
                        boxShadow: "0 2px 12px #e6f0f925",
                    }}
                >
                    <input
                        name="name"
                        placeholder="Your Name"
                        required
                        style={{
                            display: "block",
                            width: "100%",
                            marginBottom: isMobile ? 12 : 18,
                            border: "1px solid #CAD3E2",
                            borderRadius: 8,
                            fontSize: isMobile ? 15 : 16,
                            padding: isMobile ? 10 : 12,
                        }}
                    />

                    <input
                        name="user_email"
                        placeholder="Your Email"
                        type="email"
                        required
                        style={{
                            display: "block",
                            width: "100%",
                            marginBottom: isMobile ? 12 : 18,
                            border: "1px solid #CAD3E2",
                            borderRadius: 8,
                            fontSize: isMobile ? 15 : 16,
                            padding: isMobile ? 10 : 12,
                        }}
                    />

                    <input
                        name="subject"
                        placeholder="Subject"
                        required
                        style={{
                            display: "block",
                            width: "100%",
                            marginBottom: isMobile ? 12 : 18,
                            border: "1px solid #CAD3E2",
                            borderRadius: 8,
                            fontSize: isMobile ? 15 : 16,
                            padding: isMobile ? 10 : 12,
                        }}
                    />

                    <textarea
                        name="message"
                        placeholder="Your message"
                        required
                        rows={isMobile ? 4 : 5}
                        style={{
                            display: "block",
                            width: "100%",
                            marginBottom: isMobile ? 12 : 18,
                            border: "1px solid #CAD3E2",
                            borderRadius: 8,
                            fontSize: isMobile ? 15 : 16,
                            padding: isMobile ? 10 : 12,
                            resize: "vertical",
                        }}
                    />

                    <input type="hidden" name="time" value={today} />

                    <button
                        type="submit"
                        style={{
                            width: "100%",
                            background: "#7B9ACC",
                            color: "#fff",
                            fontWeight: 800,
                            fontSize: isMobile ? 15 : 17,
                            border: 0,
                            borderRadius: 10,
                            padding: isMobile ? "11px 0" : "14px 0",
                            marginTop: 8,
                            cursor: "pointer",
                            boxShadow: "0 2px 12px #7B9ACC44",
                            transition: "background .2s",
                        }}
                        onMouseEnter={(e) => (e.currentTarget.style.background = "#6A8BD6")}
                        onMouseLeave={(e) => (e.currentTarget.style.background = "#7B9ACC")}
                    >
                        Send Message
                    </button>
                </form>
            </div>

            {/* ────── Small footer note ────── */}
            <p
                style={{
                    marginTop: 40,
                    fontSize: 13,
                    color: "#7788AA",
                    textAlign: "center",
                }}
            >
                © {new Date().getFullYear()} Yaafiiee. All rights reserved.
            </p>
        </div>
    );
}
