import React, { useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Responsive device hook
function useIsMobile(breakpoint = 600) {
    const [isMobile, setIsMobile] = useState(window.innerWidth < breakpoint);
    React.useEffect(() => {
        function handleResize() { setIsMobile(window.innerWidth < breakpoint); }
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, [breakpoint]);
    return isMobile;
}

// Google Drive preview helper
function toGDrivePreviewUrl(url) {
    const match = url.match(/\/file\/d\/([^/]+)\//);
    return match ? `https://drive.google.com/file/d/${match[1]}/preview` : url;
}

// PDF Modal Popup
function PDFModal({ open, url, onClose, isMobile }) {
    return open ? (
        <div style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(60,80,120,0.16)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 2000
        }}>
            <motion.div
                initial={{ opacity: 0, scale: .88, y: 40 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.85, y: 60 }}
                transition={{ duration: 0.5 }}
                style={{
                    background: "#fff",
                    borderRadius: 18,
                    boxShadow: "0 4px 24px #0f143058",
                    padding: isMobile ? 9 : 22,
                    width: isMobile ? "99vw" : "96vw",
                    maxWidth: isMobile ? "99vw" : 620,
                    maxHeight: "92vh",
                    display: "flex",
                    flexDirection: "column"
                }}
            >
                <button
                    onClick={onClose}
                    style={{
                        marginLeft: "auto",
                        marginBottom: 8,
                        background: "#7B9ACC",
                        color: "#fff",
                        borderRadius: 7,
                        border: "none",
                        fontWeight: 700,
                        padding: isMobile ? "7px 11px" : "7px 16px",
                        fontSize: isMobile ? 15 : 17,
                        cursor: "pointer"
                    }}
                >
                    Close
                </button>
                <iframe
                    src={url}
                    title="PDF Viewer"
                    style={{
                        width: "100%",
                        height: isMobile ? "55vh" : "70vh",
                        borderRadius: 10,
                        border: "none",
                        background: "#f8fbff"
                    }}
                />
            </motion.div>
        </div>
    ) : null;
}

// Main Component
export default function InteractionDesignCase() {
    const isMobile = useIsMobile();
    const CASE_STUDY_STEPS = [/* ... paste your CASE_STUDY_STEPS array here ... */];
    const colors = [
        "#C7D8F7", "#D1E1F4", "#F2F8FF", "#EDF2FB", "#EAF6FF", "#D2E6F7", "#F3F8FC", "#F7FAFF", "#DBEAF6"
    ];

    const refs = useRef(CASE_STUDY_STEPS.map(() => React.createRef()));
    const [pdfModal, setPdfModal] = useState({ open: false, url: "" });

    const scrollToStep = idx => {
        refs.current[idx].current?.scrollIntoView({ behavior: "smooth", block: "start" });
    };

    return (
        <div style={{
            maxWidth: isMobile ? "99vw" : 820,
            margin: isMobile ? "19px auto" : "48px auto",
            padding: isMobile ? "12px 2vw" : 20,
            position: "relative"
        }}>
            <motion.h1
                initial={{ opacity: 0, y: -24, scale: 0.93 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ duration: .8 }}
                style={{
                    fontSize: isMobile ? 22 : 34,
                    color: "#7B9ACC",
                    fontWeight: 900,
                    marginBottom: isMobile ? 12 : 22,
                    letterSpacing: 0.5
                }}>
                Interaction Design Project
            </motion.h1>

            <motion.div
                initial={{ opacity: 0, y: -12 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: .4, duration: .6 }}
                style={{
                    marginBottom: isMobile ? 14 : 23,
                    fontSize: isMobile ? 13 : 17
                }}
            >
                Scroll to view each animated step, or click below to jump:
            </motion.div>

            <motion.nav
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: .6, duration: .5 }}
                style={{
                    display: "flex",
                    gap: isMobile ? 9 : 14,
                    flexWrap: "wrap",
                    marginBottom: isMobile ? 14 : 28,
                    padding: "6px 0"
                }}
            >
                {CASE_STUDY_STEPS.map((step, idx) => (
                    <motion.button
                        key={step.title}
                        whileHover={{ scale: 1.08, backgroundColor: "#E3EDFF", color: "#527DCA" }}
                        whileTap={{ scale: 0.97 }}
                        onClick={() => scrollToStep(idx)}
                        style={{
                            fontWeight: 600,
                            letterSpacing: 0.6,
                            color: "#7B9ACC",
                            background: "#F6FAFF",
                            border: "1.6px solid #D7E2FA",
                            borderRadius: 8,
                            padding: isMobile ? "7px 9px" : "7px 10px",
                            fontSize: isMobile ? 12.5 : 15,
                            cursor: "pointer",
                            transition: "all .18s",
                            boxShadow: "0 1px 4px #c0c7ef0b"
                        }}
                    >
                        {step.title}
                    </motion.button>
                ))}
            </motion.nav>

            <AnimatePresence>
                <PDFModal open={pdfModal.open} url={pdfModal.url} onClose={() => setPdfModal({ open: false, url: "" })} isMobile={isMobile} />
            </AnimatePresence>

            {CASE_STUDY_STEPS.map((step, idx) => (
                <motion.section
                    ref={refs.current[idx]}
                    key={step.title}
                    initial={{ opacity: 0, y: 40, scale: 0.97, boxShadow: "0 2px 26px #c0c7ef00" }}
                    whileInView={{
                        opacity: 1,
                        y: 0,
                        scale: 1,
                        boxShadow: "0 4px 26px #a2b6f444"
                    }}
                    whileHover={{
                        scale: 1.017,
                        boxShadow: "0 8px 28px #7b9acc22",
                        backgroundColor: colors[idx % colors.length]
                    }}
                    transition={{ delay: idx * 0.13, duration: 0.73, type: "spring" }}
                    viewport={{ once: true, amount: 0.19 }}
                    style={{
                        marginBottom: isMobile ? 24 : 38,
                        background: colors[idx % colors.length],
                        borderRadius: 14,
                        padding: isMobile ? 13 : 19,
                        boxShadow: "0 2px 13px #c0c7ef21",
                        cursor: "pointer",
                        overflowX: "auto"
                    }}>
                    <div style={{
                        fontWeight: 700,
                        fontSize: isMobile ? 17 : 24,
                        marginBottom: 6
                    }}>
                        {step.title}
                    </div>
                    <div style={{
                        marginBottom: 7,
                        color: "#232751",
                        fontSize: isMobile ? 13 : 17
                    }}>
                        {step.desc}
                    </div>
                    {/* PDF Modal Button */}
                    {step.file && step.type === "pdf" && (
                        <button
                            onClick={() => setPdfModal({ open: true, url: step.file })}
                            style={{
                                background: "#7B9ACC",
                                color: "#fff",
                                borderRadius: 6,
                                padding: isMobile ? "6px 11px" : "6px 15px",
                                fontWeight: 600,
                                fontSize: isMobile ? 12.7 : 15,
                                marginTop: 8,
                                border: "none",
                                cursor: "pointer"
                            }}>
                            View PDF
                        </button>
                    )}

                    {/* Images */}
                    {step.file && step.type === "image" && (
                        <motion.img
                            src={step.file}
                            alt={step.title}
                            initial={{ opacity: 0, scale: .94 }}
                            whileInView={{ opacity: 1, scale: 1 }}
                            transition={{ duration: .73 }}
                            style={{
                                width: isMobile ? "99vw" : "96%",
                                maxWidth: 510,
                                borderRadius: 10,
                                marginTop: 8,
                                boxShadow: "0 2px 18px #becae921",
                                objectFit: "contain"
                            }}
                        />
                    )}

                    {/* Multi Google Drive videos */}
                    {step.videos && step.type === "gdrive-multivideo" && (
                        <div style={{
                            display: "flex",
                            gap: isMobile ? 12 : 20,
                            flexWrap: "wrap",
                            flexDirection: isMobile ? "column" : "row"
                        }}>
                            {step.videos.map((video, i) => (
                                <motion.div
                                    key={video.label}
                                    initial={{ opacity: 0, scale: 0.96 }}
                                    whileInView={{ opacity: 1, scale: 1 }}
                                    transition={{ duration: .64, delay: i * 0.18 }}
                                    style={{
                                        width: isMobile ? "99vw" : 320,
                                        minWidth: isMobile ? "99vw" : 230,
                                        marginTop: 8,
                                        borderRadius: 10,
                                        overflow: "hidden",
                                        boxShadow: "0 2px 18px #becae921",
                                        background: "#f6f8fa",
                                        display: "flex",
                                        flexDirection: "column",
                                        alignItems: "center"
                                    }}
                                >
                                    <div style={{
                                        fontWeight: 600,
                                        margin: isMobile ? "10px 0 3px" : "10px 0 6px",
                                        color: "#527DCA",
                                        fontSize: isMobile ? 12.5 : 15
                                    }}>
                                        {video.label}
                                    </div>
                                    <iframe
                                        src={toGDrivePreviewUrl(video.url)}
                                        allow="autoplay"
                                        frameBorder="0"
                                        style={{
                                            width: "100%",
                                            height: isMobile ? 180 : 210,
                                            borderRadius: "10px"
                                        }}
                                        allowFullScreen
                                        title={video.label}
                                    />
                                </motion.div>
                            ))}
                        </div>
                    )}

                    {/* Figma embed */}
                    {step.file && step.type === "figma" && (
                        <motion.div
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: .72, delay: .13 }}
                            style={{
                                width: "100%",
                                minHeight: isMobile ? 230 : 400,
                                marginTop: 12,
                                borderRadius: 10,
                                boxShadow: "0 2px 12px #c7d8f733",
                                overflow: "hidden"
                            }}>
                            <iframe
                                src={step.file}
                                allowFullScreen
                                width="100%"
                                height={isMobile ? 270 : 500}
                                style={{
                                    border: "none",
                                    borderRadius: "10px",
                                    minHeight: isMobile ? 230 : 430,
                                    background: "#fff"
                                }}
                                title={step.title}
                            />
                        </motion.div>
                    )}

                    {/* Text Content */}
                    {step.content && (
                        <motion.div
                            initial={{ opacity: 0, y: 30 }}
                            whileInView={{ opacity: 1, y: 0 }}
                            transition={{ duration: .72, delay: .13 }}
                            style={{
                                fontSize: isMobile ? 12.6 : 15,
                                color: "#565",
                                background: "#F4F8FC",
                                margin: "12px 0 0",
                                padding: isMobile ? 8 : 13,
                                borderRadius: 9,
                                boxShadow: "0 2px 7px #c7d8f711"
                            }}>
                            {step.content}
                        </motion.div>
                    )}
                </motion.section>
            ))}
        </div>
    );
}
