import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Code, Search, AlertTriangle, CheckCircle, ArrowRight, FileText, Users, Globe, Zap, Shield, Eye } from 'lucide-react';

const theme = {
    primary: '#5DADE2',
    light: '#A7C7E7',
    bg: '#F8FBFF',
    text: '#1A365D',
    white: '#FFFFFF',
    success: '#10B981',
    danger: '#EF4444',
    warning: '#F59E0B',
};

const fadeIn = { hidden: { opacity: 0, y: 20 }, visible: { opacity: 1, y: 0 } };

export default function STQMFullShowcase() {
    const [activeTab, setActiveTab] = useState('overview');

    return (
        <div style={{
            minHeight: '100vh',
            background: theme.bg,
            fontFamily: "'Inter', sans-serif",
            color: theme.text,
        }}>
            {/* Hero */}
            <section style={{ padding: '80px 20px', textAlign: 'center', background: `linear-gradient(135deg, ${theme.light} 0%, ${theme.primary} 100%)`, color: 'white' }}>
                <motion.h1 initial={{ opacity: 0, y: -30 }} animate={{ opacity: 1, y: 0 }} style={{ fontSize: '3rem', fontWeight: 800, margin: 0 }}>
                    Systems Testing & Quality Management
                </motion.h1>
                <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2 }} style={{ fontSize: '1.3rem', margin: '16px 0 32px', maxWidth: '800px', marginLeft: 'auto', marginRight: 'auto' }}>
                    3 High-Impact Projects: Static Review (40 issues), Black/White-Box (37 cases), System Automation (88% pass) — Full lifecycle quality.
                </motion.p>
                <motion.div initial={{ scale: 0.8 }} animate={{ scale: 1 }} transition={{ delay: 0.4 }} style={{ display: 'flex', justifyContent: 'center', gap: '30px', flexWrap: 'wrap' }}>
                    <ImpactCard icon={<Shield />} value="40" label="Issues Found" />
                    <ImpactCard icon={<Zap />} value="85%" label="Test Coverage" />
                    <ImpactCard icon={<CheckCircle />} value="88%" label="Pass Rate" />
                </motion.div>
            </section>

            {/* Navigation */}
            <div style={{ display: 'flex', justifyContent: 'center', gap: '12px', padding: '30px 20px', flexWrap: 'wrap' }}>
                {['overview', 'project1', 'project2', 'project3', 'impact'].map((tab) => (
                    <button
                        key={tab}
                        onClick={() => setActiveTab(tab)}
                        style={{
                            padding: '12px 28px',
                            border: 'none',
                            borderRadius: '50px',
                            background: activeTab === tab ? theme.primary : theme.white,
                            color: activeTab === tab ? 'white' : theme.text,
                            fontWeight: 600,
                            cursor: 'pointer',
                            boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
                            transition: '0.3s',
                        }}
                    >
                        {tab === 'project1' ? 'Project 1: Static Review' : tab === 'project2' ? 'Project 2: Black/White-Box' : tab === 'project3' ? 'Project 3: System Test' : tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>

            {/* Content */}
            <div style={{ maxWidth: '1100px', margin: '0 auto 80px', padding: '0 20px' }}>
                {activeTab === 'overview' && <Overview />}
                {activeTab === 'project1' && <Project1 />}
                {activeTab === 'project2' && <Project2 />}
                {activeTab === 'project3' && <Project3 />}
                {activeTab === 'impact' && <Impact />}
            </div>

            {/* CTA Footer */}
            <motion.footer initial={{ opacity: 0 }} animate={{ opacity: 1 }} style={{ background: theme.primary, color: 'white', textAlign: 'center', padding: '40px 20px' }}>
                <h3 style={{ margin: '0 0 16px', fontSize: '1.5rem' }}>Ready to Deliver Quality</h3>
                <p style={{ margin: '0 0 24px', opacity: 0.9 }}>From requirements to production — I prevent bugs, not just find them.</p>
                <motion.a
                    href="/"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    style={{
                        background: 'white',
                        color: theme.primary,
                        border: 'none',
                        padding: '14px 32px',
                        borderRadius: '50px',
                        fontWeight: 600,
                        fontSize: '1.1rem',
                        cursor: 'pointer',
                        textDecoration: 'none',
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '8px',
                    }}
                >
                    Back to Portfolio <ArrowRight size={20} />
                </motion.a>
            </motion.footer>
        </div>
    );
}

// === PROJECT 1: STATIC TESTING + FMEA (FULL 20 DEFECTS + 20 RISKS) ===
function Project1() {
    const defects = [
        { id: 'ST001', cat: 'Security', issue: 'Password complexity rules undefined', page: '15a', tags: ['Incomplete', 'Ambiguous'] },
        { id: 'ST002', cat: 'Functionality', issue: 'Prerequisite override process unclear', page: 'Req #12', tags: ['Incomplete', 'Untestable'] },
        { id: 'ST003', cat: 'Performance', issue: '"Fast movements" not quantified', page: '1b', tags: ['Ambiguous'] },
        { id: 'ST004', cat: 'UI', issue: 'No mobile responsiveness mentioned', page: '4e', tags: ['Incomplete'] },
        { id: 'ST005', cat: 'Security', issue: 'Session timeout not specified', page: '15a', tags: ['Incomplete'] },
        { id: 'ST006', cat: 'Accessibility', issue: 'Color blindness not addressed', page: '11e', tags: ['Incomplete'] },
