import React, { useState } from "react";

// ----- Project Data -----
const projects = [
  {
    id: 1,
    title: "IoTBay - IoT Devices Ordering Application",
    description: "A Java Web App for ordering and managing IoT devices online.",
    tech: "Java, JSP, JDBC, MVC, MySQL",
    details: `IoTBay is an online platform for customers to purchase IoT devices like sensors, actuators, and gateways. I led the shipment management module, implementing full CRUD operations and secure data handling. The app features user registration/login, device catalog, order/payment processing, and shipment tracking, all built with MVC architecture. Improved backend performance for faster order retrieval and collaborated in an Agile 6-person team using Git for version control.`,
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=500&h=300&fit=crop"
  },
  {
    id: 2,
    title: "Interaction Design Project",
    description: "A web-based application for managing tasks and priorities.",
    tech: "Figma, UX Research",
    details: "Designed and tested collaborative task manager app following HCI principles. Research, prototyping, and iterative usability testing documented below.",
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=500&h=300&fit=crop",
    designProcess: [
      {
        step: "Journal Entry",
        detail: "Documented initial observations and pain points using reflective journal.",
        file: "/journal-entry.pdf"
      },
      {
        step: "Interview & Persona",
        detail: "Conducted interviews; created primary and secondary personas.",
        file: "/persona.pdf"
      },
      {
        step: "Problem Scenario",
        detail: "Defined main user scenarios, mapped user goals and frustrations.",
        file: ""
      },
      {
        step: "Storyboard",
        detail: "Drew narrative storyboard showing user journey with product.",
        file: "/storyboard.png"
      },
      {
        step: "Paper Prototype",
        detail: "Sketched UI, iterated design; tested with 3 users.",
        file: "/paperproto.pdf"
      },
      {
        step: "Usability Testing",
        detail: "Ran tests, gathered feedback; improved navigation and accessibility.",
        file: ""
      },
      {
        step: "DIA Video",
        detail: "Presented design, rationale, and process summary.",
        file: "/DIA - FID.mp4"
      }
    ]
  },
  {
    id: 3,
    title: "IT Project Dashboard",
    description: "Dashboard for tracking IT projects, resources, and progress.",
    tech: "ASP.NET MVC, SQL Server, Bootstrap",
    details: "Visualize all project KPIs, manage team assignments, and export weekly reports. Role-based access control.",
    image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=500&h=300&fit=crop"
  },
  {
    id: 4,
    title: "Business Analytics Portal",
    description: "Analytics tool for visualizing KPIs and insights.",
    tech: "Python (Django), PostgreSQL, Chart.js",
    details: "Interactive dashboards for business users with exportable charts and real-time data updates.",
    image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=500&h=300&fit=crop"
  }
];

// ----- Skills Data -----
const skills = [
  "JavaScript", "Python", "C#", "SQL", "HTML & CSS",
  "React", "Node.js", "ASP.NET MVC", "Django",
  "Git", "Jira", "Figma", "Project Management"
];

// ----- Main App -----
export default function App() {
  const [expandedId, setExpandedId] = useState(null);

  const handleExpand = (projectId) => {
    setExpandedId(projectId === expandedId ? null : projectId);
  };

  const cardStyle = {
    background: "#fff",
    borderRadius: 10,
    boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
    padding: 20,
    cursor: "pointer",
    transition: "transform 0.2s, box-shadow 0.2s",
    display: "flex",
    flexDirection: "column"
  };

  const cardHoverStyle = {
    transform: "scale(1.04)",
    boxShadow: "0 8px 24px rgba(0,0,0,0.15)"
  };

  return (
    <div style={{ fontFamily: "Segoe UI,Arial,sans-serif", background: "#f7f7f7", minHeight: "100vh", margin: 0 }}>
      {/* Navigation */}
      <nav style={{
        background: "#25274d",
        color: "#fff",
        padding: "20px 0",
        display: "flex",
        justifyContent: "center",
        gap: "40px",
        fontWeight: "bold",
        letterSpacing: "1px"
      }}>
        {["About", "Projects", "Skills", "Contact"].map(section => (
          <a key={section} href={`#${section.toLowerCase()}`} style={{ color: "#fff", textDecoration: "none" }}>{section}</a>
        ))}
      </nav>

      {/* Main Content */}
      <main style={{ maxWidth: 900, margin: "40px auto", padding: "0 20px" }}>
        {/* About Section */}
        <section id="about" style={{ marginBottom: 60 }}>
          <h1 style={{ fontWeight: 800, fontSize: 42, marginBottom: 12 }}>Yafie Farabi</h1>
          <p style={{ fontSize: 18, color: "#444", lineHeight: 1.6 }}>
            Software Engineer & Business Analyst specializing in web development and MVC-based applications. Experienced in IT project management, database design, and building end-to-end solutions with a focus on user-friendly interfaces and scalable architectures.
          </p>
        </section>

        {/* Projects Section */}
        <section id="projects" style={{ marginBottom: 60 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Projects</h2>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))", gap: 24 }}>
            {projects.map(project => (
              <ProjectCard
