// ----- Project Card Component -----
function ProjectCard({ project, expandedId, handleExpand, cardStyle, cardHoverStyle }) {
  const [isHovered, setIsHovered] = useState(false);
  const isExpanded = project.id === expandedId;

  // Optional: GitHub link only for IoTBay
  const githubLink = project.id === 1 ? "https://github.com/jaeyunjks/IOTBay.git" : null;

  return (
    <div
      style={{
        ...cardStyle,
        ...(isHovered ? cardHoverStyle : {}),
        border: isExpanded ? "2px solid #25274d" : "1px solid #eee"
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={() => handleExpand(project.id)}
    >
      <img
        src={project.image}
        alt={project.title}
        style={{ width: "100%", borderRadius: 8, marginBottom: 14, height: 140, objectFit: "cover" }}
      />
      <h3 style={{ margin: "8px 0", fontSize: 22 }}>{project.title}</h3>
      <p style={{ color: "#555", marginBottom: 8 }}>{project.description}</p>
      <span style={{
        display: "inline-block",
        fontSize: 13,
        fontWeight: 600,
        background: "#25274d",
        color: "#fff",
        borderRadius: 13,
        padding: "3px 12px",
        marginBottom: 8
      }}>{project.tech}</span>

      {isExpanded && (
        <div style={{
          marginTop: 12,
          color: "#25274d",
          background: "#e6e8fa",
          borderRadius: 8,
          padding: 12,
          fontSize: 15,
          lineHeight: 1.5,
          animation: "fadeIn 0.25s ease-in-out"
        }}>
          {project.details}
          {/* Show designProcess inside expanded card if exist */}
          {project.designProcess && (
            <div style={{ marginTop: 18 }}>
              <h4 style={{ color: "#25274d", fontWeight: 700, fontSize: 20, marginBottom: 10 }}>Design Process</h4>
              <ol style={{ paddingLeft: 16, margin: 0 }}>
                {project.designProcess.map((step, idx) => (
                  <li key={step.step} style={{ marginBottom: 14 }}>
                    <span style={{ fontWeight: 600 }}>{idx + 1}. {step.step}: </span>
                    <span>{step.detail}</span>
                    {step.file &&
                      (step.file.endsWith(".png") || step.file.endsWith(".jpg") || step.file.endsWith(".jpeg")) ? (
                      <img
                        src={step.file}
                        alt={step.step}
                        style={{
                          width: "90%",
                          maxWidth: 350,
                          borderRadius: 6,
                          marginTop: 8,
                          boxShadow: "0 2px 8px rgba(0,0,0,0.12)"
                        }}
                      />
                    ) : step.file && step.file.endsWith(".pdf") ? (
                      <a
                        href={step.file}
                        target="_blank"
                        rel="noreferrer"
                        style={{
                          background: "#25274d",
                          color: "#fff",
                          borderRadius: 6,
                          padding: "7px 14px",
                          marginLeft: 10,
                          textDecoration: "none",
                          fontWeight: 600
                        }}
                      >
                        View PDF
                      </a>
                    ) : step.file && step.file.endsWith(".mp4") ? (
                      <video
                        src={step.file}
                        controls
                        style={{
                          width: "95%",
                          maxWidth: 400,
                          borderRadius: 8,
                          marginTop: 12,
                          boxShadow: "0 2px 8px rgba(0,0,0,0.10)"
                        }}
                      >
                        Your browser does not support the video tag.
                      </video>
                    ) : null}
                  </li>
                ))}
              </ol>
            </div>
          )}
          {githubLink && (
            <div style={{ marginTop: 10 }}>
              <a
                href={githubLink}
                target="_blank"
                rel="noreferrer"
                style={{
                  textDecoration: "none",
                  color: "#fff",
                  background: "#25274d",
                  padding: "6px 12px",
                  borderRadius: 6,
                  fontWeight: 600,
                  display: "inline-block"
                }}
                onClick={(e) => e.stopPropagation()}
              >
                View Code
              </a>
            </div>
          )}
        </div>
      )}

      <div style={{ marginTop: 10 }}>
        <button style={{
          background: "#25274d",
          color: "#fff",
          border: "none",
          borderRadius: 6,
          padding: "6px 12px",
          cursor: "pointer"
        }}>
          {isExpanded ? "Collapse" : "View Details"}
        </button>
      </div>
    </div>
  );
}
