import React, { useState } from "react";

// --- Glass Card Utility ---
function GlassCard({ children, style = {}, ...rest }) {
    return (
        <div
            style={{
                background: "rgba(255,255,255,0.75)",
                borderRadius: 19,
                boxShadow: "0 2px 19px #b1bee755",
                border: "1.5px solid #e5f0fd",
                padding: "18px 16px",
                margin: "0 0 18px 0",
                transition: "transform .18s, box-shadow .19s",
                ...style
            }}
            {...rest}
        >
            {children}
        </div>
    );
}

// --- Modal Component ---
function Modal({ open, onClose, children }) {
    if (!open) return null;
    return (
        <div style={{
            position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
            background: 'rgba(40,72,180,0.19)', display: 'flex',
            alignItems: 'center', justifyContent: 'center', zIndex: 99
        }}>
            <div style={{
                background: 'rgba(255,255,255,0.93)',
                borderRadius: 22,
                maxWidth: 460, minHeight: 120,
                boxShadow: '0 14px 44px #22333329',
                padding: '32px 20px',
                position: 'relative',
                animation: 'fadeInModal .55s'
            }}>
                <button
                    style={{
                        position: 'absolute', top: 9, right: 19, fontSize: 27,
                        background: 'none', border: 'none', color: '#1265d8', fontWeight: 900, cursor: 'pointer'
                    }}
                    onClick={onClose}
                ></button>
                {children}
            </div>
            <style>{`
        @keyframes fadeInModal {
          from { opacity:0; transform:scale(.91);}
          to { opacity:1; transform:scale(1);}
        }
      `}</style>
        </div>
    );
}

// --- Animated icon (simple SVG) ---
function StatIcon({ type }) {
    const color = '#2073cd';
    switch (type) {
        case 'micro': return <svg width="32" height="32"><circle cx="16" cy="16" r="11" stroke={color} strokeWidth="3.4" fill="none" /><circle cx="16" cy="16" r="5" fill={color} opacity=".2" /><circle cx="21" cy="11" r="2.8" fill={color} opacity=".43" /><circle cx="11" cy="20" r="2.5" fill={color} opacity=".15" /></svg>;
        case 'scale': return <svg width="32" height="32"><rect x="5" y="20" width="6" height="7.5" rx="2.5" fill={color} opacity=".18" /><rect x="12" y="10" width="6.3" height="17.5" rx="2.5" fill={color} opacity=".669" /><rect x="20" y="3" width="6" height="25" rx="2.3" fill={color} opacity=".36" /></svg>;
        case 'uptime': return <svg width="32" height="32"><circle cx="16" cy="16" r="11" stroke={color} strokeWidth="2.3" fill="none" /><path d="M16 7v9l7 7" stroke={color} strokeWidth="2.6" fill="none" /></svg>;
        case 'latency': return <svg width="32" height="32"><rect x="6" y="21" width="20" height="5.5" rx="2.3" fill={color} opacity=".22" /><polyline points="6,21 13,13 20,27 26,9" stroke={color} strokeWidth="2.4" fill="none" /></svg>;
        default: return null;
    }
}

function SectionTitle({ subtitle, children }) {
    return (
        <div style={{ marginBottom: 14, marginTop: 0, textAlign: "center" }}>
            <div style={{ color: "#2073cd", fontWeight: 700, fontSize: 15, opacity: 0.77 }}>{subtitle}</div>
            <div style={{ fontWeight: 900, fontSize: 28, marginTop: 3 }}>{children}</div>
        </div>
    );
}

