import React, { useState } from "react";
import { motion } from "framer-motion";

// --- LightboxModal Component ---
function LightboxModal({ open, onClose, children }) {
    if (!open) return null;
    return (
        <div className="fixed inset-0 z-50 bg-black/40 flex items-center justify-center">
            <div className="bg-white/80 rounded-2xl p-6 max-w-lg shadow-2xl relative backdrop-blur-md">
                <button
                    className="absolute top-3 right-4 text-2xl text-indigo-300 hover:text-indigo-500"
                    onClick={onClose}
                >
                    ×
                </button>
                {children}
            </div>
        </div>
    );
}

// --- Parallax Diagram Placeholder Component ---
function ParallaxDiagram() {
    // In real use, replace with animated Framer Motion/React Parallax content
    return (
        <motion.div
            initial={{ y: 60, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.9, type: "spring" }}
            className="mt-8 mb-20 flex justify-center items-center w-full h-44 bg-gradient-to-br from-blue-100 to-indigo-50 rounded-3xl shadow-lg border border-white/35 backdrop-blur-md"
        >
            <span className="text-indigo-500 font-bold text-xl">[Parallax Diagram – System Views Placeholder]</span>
        </motion.div>
    );
}

// --- Demo Video Modal Placeholder ---
function DemoVideoModal({ open, onClose }) {
    return (
        <LightboxModal open={open} onClose={onClose}>
            <div className="w-[320px] h-[180px] bg-black rounded-xl flex items-center justify-center text-white font-bold text-lg">
                {/* Replace with embedded video */}
                Demo Video
            </div>
        </LightboxModal>
    );
}

// --- Main Page Component ---
export default function EventManagementCaseStudy() {
    const [showDiagramModal, setShowDiagramModal] = useState(false);
    const [showVideoModal, setShowVideoModal] = useState(false);

    return (
        <div className="min-h-screen w-full bg-gradient-to-br from-blue-50 to-indigo-100 flex flex-col items-center pb-24">

            {/* Header / Hero Section */}
            <section className="w-full py-14 flex flex-col items-center bg-gradient-to-b from-blue-100 to-white relative">
                <motion.div
                    initial={{ opacity: 0, y: 32 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, type: 'spring' }}
                    className="text-center mb-5"
                >
                    <h1 className="font-black text-4xl md:text-5xl text-indigo-700 drop-shadow-lg">EventX – Scalable Event Management & Ticketing</h1>
                    <p className="text-lg font-medium text-indigo-400 mt-4 max-w-2xl mx-auto">
                        Interactive architecture case study for real-world ticketing systems.
                        Modern, secure, and scalable event platforms.
                    </p>
                </motion.div>
                <div className="flex gap-5 mt-8 mb-7 justify-center">
                    <button
                        onClick={() => setShowDiagramModal(true)}
                        className="bg-indigo-500/70 hover:bg-indigo-600/90 text-white rounded-xl px-8 py-3 shadow-lg font-semibold backdrop-blur-md transition"
                    >
                        Explore Architecture
                    </button>
                    <button
                        onClick={() => setShowVideoModal(true)}
                        className="bg-white/90 border border-indigo-300 hover:bg-indigo-50 text-indigo-500 font-semibold rounded-xl px-8 py-3 shadow transition backdrop-blur-md"
                    >
                        Watch Demo
                    </button>
                </div>
                {/* Quick Facts Glassmorphism Panel */}
                <motion.div
                    initial={{ opacity: 0, y: 24 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6 }}
                    className="flex flex-wrap gap-4 justify-center mt-2"
                >
                    {[
                        ["Stack", "React, Tailwind, Node.js, AWS"],
                        ["User Types", "Organiser, Attendee, Partner"],
                        ["Concurrency", "10K+ events/month"],
                        ["Mobile Apps", "iOS & Android"],
                    ].map(([title, text]) => (
                        <div key={title} className="glass-card px-5 py-2 rounded-2xl shadow max-w-xs backdrop-blur-md border border-white/30">
                            <span className="font-semibold text-indigo-600">{title}:</span> <span className="text-indigo-900">{text}</span>
                        </div>
                    ))}
                </motion.div>
            </section>

            {/* Modals */}
            <DemoVideoModal open={showVideoModal} onClose={() => setShowVideoModal(false)} />
            <LightboxModal open={showDiagramModal} onClose={() => setShowDiagramModal(false)}>
                <ParallaxDiagram />
                <div className="mt-5 text-center text-indigo-700 font-semibold">Detailed system architecture diagram and separation of concerns.</div>
            </LightboxModal>

            {/* Overview Section */}
            <section className="w-full max-w-5xl px-3 md:px-0 mt-14">
                <motion.div
                    initial={{ opacity: 0, y: 32 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.75 }}
                >
                    <h2 className="text-2xl md:text-3xl font-bold text-indigo-700 mb-7">Project Overview & Platform Goals</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        {/* Goals/Requirements Card */}
                        <div className="glass-card px-6 py-5 rounded-3xl shadow backdrop-blur-md border border-white/30">
                            <h3 className="font-semibold text-indigo-600 mb-3">Key Requirements</h3>
                            <ul className="text-indigo-900 list-disc ml-5 space-y-2 text-base font-medium">
                                <li>Rich media support (images & video for event pages)</li>
                                <li>Dynamic ticket pricing & seat selection</li>
                                <li>Social media sharing & login integration</li>
                                <li>Real-time ticket tracking & availability</li>
                                <li>Secure payment gateway (PCI DSS compliance)</li>
                                <li>Post-event analytics dashboard</li>
                                <li>Mobile app experience</li>
                            </ul>
                        </div>
                        {/* Stakeholders Card */}
                        <div className="glass-card px-6 py-5 rounded-3xl shadow backdrop-blur-md border border-white/30">
                            <h3 className="font-semibold text-indigo-600 mb-3">Stakeholder Analysis</h3>
                            <div className="grid grid-cols-2 gap-2 text-indigo-900 font-medium">
                                <div><span className="font-semibold text-indigo-500">Organisers:</span> Event setup, ticket tools</div>
                                <div><span className="font-semibold text-indigo-500">Attendees:</span> Booking, mobile check-in</div>
                                <div><span className="font-semibold text-indigo-500">Payment Providers:</span> Secure processing</div>
                                <div><span className="font-semibold text-indigo-500">Marketing Partners:</span> Social sharing, promotions</div>
                            </div>
                        </div>
                    </div>
                </motion.div>
            </section>

            {/* Problem Statement Section */}
            <section className="w-full max-w-3xl px-3 mt-16">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.7 }}
                    className="glass-card px-7 py-5 rounded-3xl shadow backdrop-blur-md border border-white/30"
                >
                    <h2 className="text-xl font-bold text-indigo-700 mb-4">Problem Statement & Design Trade-offs</h2>
                    <p className="text-indigo-900 text-base font-medium mb-2">
                        Traditional event management systems struggle with:
                    </p>
                    <ul className="list-disc ml-5 text-indigo-800 space-y-1 font-medium">
                        <li>Handling flexible event structures vs. control/security</li>
                        <li>Implementing dynamic pricing, seat maps, and ticket rules</li>
                        <li>Scaling for thousands of real-time users/events</li>
                        <li>Balancing payment security and user convenience</li>
                        <li>Achieving meaningful post-event analytics</li>
                    </ul>
                    <p className="mt-3 text-indigo-600">Trade-offs are found between adaptability, cost, capacity, and security at every layer.</p>
                </motion.div>
            </section>

            {/* Architecture Section */}
            <section className="w-full max-w-6xl px-3 mt-16">
                <motion.div
                    initial={{ opacity: 0, y: 32 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8 }}
                >
                    <h2 className="text-2xl md:text-3xl font-bold text-indigo-700 mb-7">System Architecture Views</h2>
                    <ParallaxDiagram />
                    <div className="flex flex-wrap gap-6 justify-center mt-2">
                        {[
                            ["Context", "Actors, touchpoints, integration boundary"],
                            ["Container", "Microservices, API Gateway, BFF"],
                            ["Component", "Front-end, back-end, database"],
                            ["Deployment", "Cloud infra, serverless, scaling"]
                        ].map(([title, text]) => (
                            <div key={title} className="glass-card px-7 py-4 rounded-2xl shadow backdrop-blur-md border border-white/30 min-w-[180px]">
                                <span className="font-semibold text-indigo-700">{title} View</span>
                                <p className="text-indigo-900 text-sm">{text}</p>
                                <button className="text-indigo-500 underline text-xs mt-3" onClick={() => setShowDiagramModal(true)}>
                                    See Diagram
                                </button>
                            </div>
                        ))}
                    </div>
                    {/* Quality attributes & scenarios */}
                    <div className="mt-8 text-indigo-800">
                        <div className="font-semibold mb-2">Quality Attributes:</div>
                        <div className="flex flex-wrap gap-3">
                            {["Security", "Scalability", "Usability", "Performance", "Maintainability"].map(attr => (
                                <span key={attr} className="glass-card px-3 py-1 rounded-xl border border-white/30 text-indigo-600 font-bold text-sm">
                                    {attr}
                                </span>
                            ))}
                        </div>
                        <div className="mt-2 text-sm">
                            <span className="font-medium text-indigo-900">Scenario Example:</span> "Spike in mobile ticket check-ins – system must scale API and update seat map in real time."
                        </div>
                    </div>
                </motion.div>
            </section>

            {/* Architectural Patterns & Components Section */}
            <section className="w-full max-w-6xl px-3 mt-16">
                <motion.div
                    initial={{ opacity: 0, y: 34 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.7 }}
                >
                    <h2 className="text-2xl md:text-3xl font-bold text-indigo-700 mb-7">Patterns & Major Components</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-9">
                        {/* Glassmorphism cards for patterns */}
                        {[
                            ["Microservices", "Separation of concerns, domain-driven scalable architecture"],
                            ["Event-Driven", "High-availability async updates, message bus, real-time CQRS"],
                            ["API Gateway/BFF", "Front-end tailored endpoints, mobile performance"],
                            ["Serverless", "Auto-scaled infra, cloud-native ticket/service workers"],
                        ].map(([title, text]) => (
                            <div key={title} className="glass-card px-6 py-5 rounded-3xl shadow backdrop-blur-md border border-white/30">
                                <span className="font-bold text-indigo-600">{title}</span>
                                <p className="text-indigo-900 text-sm">{text}</p>
                            </div>
                        ))}
                        {/* High-level component map */}
                        <div className="glass-card col-span-1 md:col-span-2 lg:col-span-1 px-6 py-5 rounded-3xl shadow border backdrop-blur-md border-white/30">
                            <span className="font-bold text-indigo-600">Component Map</span>
                            <div className="mt-2 text-indigo-900 text-sm">Front-end, API Gateway, Microservices, Messaging Queue, Cloud infra, Database Cluster</div>
                            <button className="text-indigo-500 underline text-xs mt-3" onClick={() => setShowDiagramModal(true)}>Show Structure</button>
                        </div>
                    </div>
                    <div className="mt-7 text-indigo-800">
                        <span className="font-medium">Trade-offs:</span> "Microservices ease scaling/maintenance but add deployment complexity. Event-driven boosts latency, requires careful contract design."
                    </div>
                </motion.div>
            </section>

            {/* Contributions, Results, & Reflection Section */}
            <section className="w-full max-w-3xl px-3 mt-20 mb-12">
                <motion.div
                    initial={{ opacity: 0, y: 15 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.65 }}
                    className="glass-card px-6 py-7 rounded-3xl shadow border backdrop-blur-md border-white/30"
                >
                    <h2 className="text-xl font-bold text-indigo-700 mb-4">Contribution, Results & Reflection</h2>
                    <ul className="space-y-2 text-indigo-900 font-medium">
                        <li><b>Role:</b> Designed architecture across all views, implemented key microservices & front-end, led stakeholder analysis.</li>
                        <li><b>Results:</b> Platform supports 10,000+ concurrent attendees per event, real-time ticket map, and secure payments (99.9% uptime).</li>
                        <li><b>Quality Metrics:</b> Achieved PCI DSS compliance, <span className="text-indigo-600">sub-second ticket processing</span> at peak.</li>
                        <li><b>Reflection:</b> Learned importance of contract-first API, trade-offs between flexibility and control, improved deployment automation with serverless infra.</li>
                        <li><b>Techniques Used:</b> Parallax scroll, grid layout, glassmorphism, Framer Motion animations, responsive mobile/desktop modals.</li>
                    </ul>
                </motion.div>
            </section>
        </div>
    );
}

