import React, { useState } from "react";

const projects = [
  {
    id: 1,
    title: "TaskManager Pro",
    description: "A web-based application for managing tasks and priorities.",
    tech: "React, Node.js, MongoDB, Express",
    details: "This app allows teams to collaboratively manage tasks, assign priorities, and track progress easily. Integrated with Slack and Google Calendar.",
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=500&h=300&fit=crop"
  },
  {
    id: 2,
    title: "IT Project Dashboard",
    description: "Dashboard for tracking IT projects, resources, and progress.",
    tech: "ASP.NET MVC, SQL Server, Bootstrap",
    details: "Visualize all project KPIs, manage team assignments, and export weekly reports. Role-based access control.",
    image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=500&h=300&fit=crop"
  },
  {
    id: 3,
    title: "Business Analytics Portal",
    description: "Analytics tool for visualizing KPIs and insights.",
    tech: "Python (Django), PostgreSQL, Chart.js",
    details: "Interactive dashboards for business users with exportable charts and real-time data updates.",
    image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=500&h=300&fit=crop"
  }
];

const skills = [
  "JavaScript", "Python", "C#", "SQL", "HTML & CSS",
  "React", "Node.js", "ASP.NET MVC", "Django",
  "Git", "Jira", "Figma", "Project Management"
];

export default function App() {
  const [expandedId, setExpandedId] = useState(null);

  const handleExpand = (projectId) => {
    setExpandedId(projectId === expandedId ? null : projectId);
  };

  // Basic CSS-in-JS styling for animation
  const cardStyle = {
    background: "#fff",
    borderRadius: 8,
    boxShadow: "0 2px 10px #eee",
    padding: 18,
    marginBottom: 4,
    cursor: "pointer",
    transition: "transform 0.18s cubic-bezier(.2,1,.22,1), box-shadow 0.18s cubic-bezier(.2,1,.22,1)"
  };

  const cardHoverStyle = {
    transform: "scale(1.03)",
    boxShadow: "0 8px 28px #ddd"
  };

  return (
    <div style={{
      fontFamily: "Segoe UI,Arial,sans-serif",
      background: "#fcfcfc",
      minHeight: "100vh",
      color: "#222",
      margin: 0
    }}>
      <nav style={{
        background: "#25274d", color: "#fff", padding: "20px 0",
        display: "flex", justifyContent: "center", gap: "40px",
        fontWeight: "bold", letterSpacing: "1px"
      }}>
        <a href="#about" style={{ color: "#fff", textDecoration: "none" }}>About</a>
        <a href="#projects" style={{ color: "#fff", textDecoration: "none" }}>Projects</a>
        <a href="#skills" style={{ color: "#fff", textDecoration: "none" }}>Skills</a>
        <a href="#contact" style={{ color: "#fff", textDecoration: "none" }}>Contact</a>
      </nav>
      <main style={{ maxWidth: 800, margin: "30px auto" }}>
        {/* About */}
        <section id="about" style={{ marginBottom: 40 }}>
          <h1 style={{ fontWeight: 800, fontSize: 40, margin: "14px 0" }}>Yafie Farabi</h1>
          <p style={{ fontSize: 18, color: "#444" }}>
            Software Engineer &amp; Business Analyst with experience in web development, IT project management, and MVC-based applications. Passionate about building robust business solutions.
          </p>
        </section>
        {/* Projects */}
        <section id="projects" style={{ marginBottom: 40 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Projects</h2>
          <div style={{
            display: "grid", gridTemplateColumns: "1fr 1fr", gap: 22
          }}>
            {projects.map(p => (
              <ProjectCard
                key={p.id}
                project={p}
                expandedId={expandedId}
                handleExpand={handleExpand}
                cardStyle={cardStyle}
                cardHoverStyle={cardHoverStyle}
              />
            ))}
          </div>
        </section>
        {/* Skills */}
        <section id="skills" style={{ marginBottom: 40 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Skills</h2>
          <div style={{
            display: "flex", flexWrap: "wrap", gap: 8
          }}>
            {skills.map(skill => (
              <span key={skill} style={{
                padding: "6px 14px", background: "#e2eafc", color: "#25274d",
                borderRadius: 8, fontWeight: 600, marginBottom: 6, fontSize: 16
              }}>{skill}</span>
            ))}
          </div>
        </section>
        {/* Contact */}
        <section id="contact">
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20 }}>Contact</h2>
          <div style={{ fontSize: 18 }}>
            <div>
              üìß Email: <a href="mailto:yafie.farabi@email.com">yafie.farabi@email.com</a>
            </div>
            <div>
              üíº LinkedIn: <a href="https://www.linkedin.com/in/yafiefarabi" target="_blank" rel="noreferrer">linkedin.com/in/yafiefarabi</a>
            </div>
            <div>
              üêô GitHub: <a href="https://github.com/yafiefarabi" target="_blank" rel="noreferrer">github.com/yafiefarabi</a>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

// ----- Project Card Component with Hover Animation & Expand -----

function ProjectCard({ project, expandedId, handleExpand, cardStyle, cardHoverStyle }) {
  const [isHovered, setIsHovered] = useState(false);
  const isExpanded = project.id === expandedId;

  return (
    <div
      style={isHovered ? { ...cardStyle, ...cardHoverStyle, border: "2px solid #25274d" }
        : { ...cardStyle, border: isExpanded ? "2px solid #25274d" : "none" }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={() => handleExpand(project.id)}
    >
      <img src={project.image} alt={project.title} style={{ width: "100%", borderRadius: 5, marginBottom: 12, height: 120, objectFit: "cover" }} />
      <h3 style={{ margin: "8px 0", fontSize: 22 }}>{project.title}</h3>
      <p style={{ color: "#555", marginBottom: 6 }}>{project.description}</p>
      <span style={{
        display: "inline-block", fontSize: 13, fontWeight: 600,
        background: "#25274d", color: "#fff",
        borderRadius: 13, padding: "2px 12px"
      }}>{project.tech}</span>
      {isExpanded && (
        <div style={{
          marginTop: 14, color: "#25274d", background: "#e6e8fa", borderRadius: 7, padding: 12, fontSize: 15,
          animation: "fadeIn 0.22s ease-in"
        }}>
          {project.details}
        </div>
      )}
      <div style={{ fontSize: 13, color: "#888", marginTop: 9 }}>
        {isExpanded ? "Click to collapse" : "Click to see more"}
      </div>
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(8px); }
            to { opacity: 1; transform: translateY(0); }
          }
        `}
      </style>
    </div>
  );
}
