import React, { useState } from "react";
import { Link } from "react-router-dom";

// Responsive device hook
function useIsMobile(breakpoint = 600) {
    const [isMobile, setIsMobile] = useState(window.innerWidth < breakpoint);
    React.useEffect(() => {
        function handleResize() { setIsMobile(window.innerWidth < breakpoint); }
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, [breakpoint]);
    return isMobile;
}

const PROJECTS = [
    // ...your projects array as before...
];

export default function PortfolioPage() {
    const [expanded, setExpanded] = useState(null);
    const isMobile = useIsMobile();

    return (
        <div style={{
            maxWidth: isMobile ? "99vw" : 900,
            margin: isMobile ? "18px auto" : "48px auto",
            padding: isMobile ? "0 3vw" : "0 17px"
        }}>
            <h1 style={{
                fontWeight: 900,
                fontSize: isMobile ? 24 : 36,
                color: "#7B9ACC",
                marginBottom: isMobile ? 14 : 28,
                textAlign: isMobile ? "center" : "left"
            }}>
                My Portfolio
            </h1>
            <div style={{
                display: "grid",
                gridTemplateColumns: isMobile ? "1fr" : "1fr 1fr",
                gap: isMobile ? 18 : 32
            }}>
                {PROJECTS.map(p => (
                    <div
                        key={p.id}
                        style={{
                            background: "#EDF2FB",
                            borderRadius: 18,
                            boxShadow: "0 2px 16px #c0c7ef17",
                            padding: isMobile ? 13 : 21,
                            cursor: "pointer",
                            border: expanded === p.id ? "2.2px solid #7B9ACC" : "1.2px solid #e3e8f2",
                            marginBottom: isMobile ? 8 : 0
                        }}
                        onClick={() => setExpanded(expanded === p.id ? null : p.id)}
                    >
                        <img src={p.img} alt={p.title}
                            style={{
                                width: "100%", borderRadius: 11,
                                maxHeight: isMobile ? 130 : 155, marginBottom: 11,
                                objectFit: "cover"
                            }} />
                        <div style={{
                            fontWeight: 700, fontSize: isMobile ? 16.5 : 22,
                            margin: "9px 0", color: "#232751"
                        }}>{p.title}</div>
                        <div style={{
                            color: "#7B9ACC", fontWeight: 600, fontSize: isMobile ? 13 : 15,
                            letterSpacing: 1, marginBottom: 7
                        }}>
                            {p.tech && p.tech.join(" · ")}
                        </div>
                        <div style={{
                            fontSize: isMobile ? 13.2 : 15, opacity: .78, marginBottom: 8
                        }}>{p.summary}</div>
                        <div style={{
                            fontSize: isMobile ? 11 : 14, color: "#798", marginBottom: 9
                        }}>
                            <i>Click for detail</i>
                        </div>
                        {expanded === p.id && (
                            <div
                                style={{
                                    marginTop: 8,
                                    background: "#FAFCFF",
                                    color: "#232751",
                                    fontSize: isMobile ? 12.3 : 15,
                                    lineHeight: 1.6,
                                    padding: isMobile ? "10px 8px" : "14px 12px",
                                    borderRadius: 9,
                                    boxShadow: "0 2px 9px #ddeaf8"
                                }}
                            >
                                {typeof p.details === "string" ? (
                                    <div style={{ marginBottom: 10 }}>{p.details}</div>
                                ) : (
                                    p.details
                                )}
                                {p.github && (
                                    <a
                                        href={p.github}
                                        target="_blank"
                                        rel="noreferrer"
                                        style={{
                                            background: "#7B9ACC",
                                            color: "#fff",
                                            borderRadius: 9,
                                            textDecoration: "none",
                                            fontWeight: 700,
                                            padding: isMobile ? "6px 12px" : "7px 18px",
                                            marginRight: 14,
                                            fontSize: isMobile ? 12 : 15,
                                            display: "inline-block",
                                            marginTop: isMobile ? 7 : 10
                                        }}
                                    >
                                        View Code
                                    </a>
                                )}
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}
