import React, { useRef, useState } from "react";
import { motion } from "framer-motion";

// Utility: Scroll to a section
function scrollToRef(ref) {
    if (ref && ref.current) ref.current.scrollIntoView({ behavior: "smooth" });
}

// Main
export default function EventManagementCaseStudy() {
    const [modalOpen, setModalOpen] = useState(false);
    const projectOverviewRef = useRef();

    // Responsive
    const mobile = window.innerWidth <= 700;

    // Project Overview Features
    const features = [
        {
            label: "Multimedia Support",
            desc: "Rich media content for events"
        },
        {
            label: "Dynamic Pricing",
            desc: "Flexible pricing strategies"
        },
        {
            label: "Social Integration",
            desc: "Seamless social media sharing"
        },
        {
            label: "Real-time Tracking",
            desc: "Live ticket availability updates"
        },
        {
            label: "Secure Payments",
            desc: "PCI-compliant transactions"
        },
        {
            label: "Analytics",
            desc: "Post-event insights & metrics"
        },
        {
            label: "Mobile App",
            desc: "Native iOS & Android apps"
        }
    ];

    // Stakeholders
    const stakeholders = [
        {
            label: "Event Organisers",
            desc: "Need tools to create, manage, and promote events with ease"
        },
        {
            label: "Attendees",
            desc: "Expect seamless booking, mobile tickets, and real-time updates"
        },
        {
            label: "Payment Providers",
            desc: "Require secure, compliant integration for transactions"
        },
        {
            label: "Marketing Partners",
            desc: "Want analytics and social media integration for promotion"
        }
    ];

    return (
        <div style={{
            minHeight: "100vh",
            width: "100vw",
            background: "linear-gradient(120deg, #EAF6FF 0%, #D2E6F7 41%, #EDF2FB 100%)",
            fontFamily: 'Segoe UI, Arial, sans-serif'
        }}>
            <div style={{
                maxWidth: 1200,
                margin: "0 auto",
                padding: mobile ? "2.2rem 0.5rem 2rem 0.5rem" : "3rem 1.5rem 3rem 1.5rem",
                boxSizing: "border-box"
            }}>
                {/* ---- HERO ---- */}
                <motion.div
                    initial={{ opacity: 0, y: -38 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.7 }}
                    style={{
                        textAlign: "center",
                        marginBottom: mobile ? 26 : 36
                    }}>
                    <span style={{
                        display: "inline-block",
                        marginTop: mobile ? 10 : 0,
                        padding: "5px 18px",
                        borderRadius: 99,
                        background: "#e6efff",
                        color: "#2073cd",
                        fontWeight: 600,
                        fontSize: 13
                    }}>Software Architecture Case Study</span>
                    <h1 style={{
                        fontWeight: 900,
                        fontSize: mobile ? 30 : 44,
                        color: "#2073cd",
                        margin: mobile ? "15px 0 12px 0" : "22px 0 12px 0",
                        letterSpacing: ".5px"
                    }}>
                        Event Management<br />& Ticketing Platform
                    </h1>
                    <div style={{
                        color: "#435a7d",
                        fontSize: mobile ? 15 : 19,
                        marginBottom: mobile ? 14 : 19,
                        lineHeight: 1.5
                    }}>
                        A comprehensive architectural solution for scalable event management, real-time ticketing, and seamless user experiences
                    </div>
                    <div style={{
                        display: 'flex',
                        justifyContent: 'center',
                        gap: mobile ? 11 : 17,
                        marginBottom: mobile ? 14 : 20,
                        flexWrap: "wrap"
                    }}>
                        <motion.button
                            whileHover={{ scale: 1.07 }}
                            whileTap={{ scale: 0.92 }}
                            style={{
                                background: "#2073cd",
                                color: "#fff",
                                fontWeight: 700,
                                fontSize: mobile ? 14 : 17,
                                borderRadius: 13,
                                padding: mobile ? "10px 20px" : "12px 32px",
                                border: "none",
                                boxShadow: "0 3px 14px #2073cd39",
                                cursor: "pointer",
                                marginRight: 4,
                                marginBottom: mobile ? 8 : 0,
                            }}
                            onClick={() => setModalOpen(true)}
                        >
                            Watch Demo
                        </motion.button>
                        <motion.button
                            whileHover={{ scale: 1.07 }}
                            whileTap={{ scale: 0.94 }}
                            style={{
                                background: "#fff",
                                color: "#2073cd",
                                fontWeight: 700,
                                fontSize: mobile ? 14 : 17,
                                borderRadius: 13,
                                padding: mobile ? "10px 20px" : "12px 32px",
                                border: "2.5px solid #e0eafd",
                                boxShadow: "0 2px 10px #d8e8ff90",
                                cursor: "pointer",
                                marginBottom: mobile ? 8 : 0,
                            }}
                            onClick={() => scrollToRef(projectOverviewRef)}
                        >
                            Explore Architecture
                        </motion.button>
                    </div>
                    {/* Quick stats */}
                    <div style={{
                        display: 'flex', gap: mobile ? 8 : 16, flexWrap: 'wrap', justifyContent: 'center', marginBottom: mobile ? 7 : 14
                    }}>
                        {[
                            { label: "Microservices", value: "12+" },
                            { label: "Scalability", value: "10K TPS" },
                            { label: "Availability", value: "99.9%" },
                            { label: "Latency", value: "<100ms" }
                        ].map((stat, i) =>
                            <motion.div
                                whileHover={{ scale: 1.08, boxShadow: "0 9px 34px #5D81B722" }}
                                key={stat.label} style={{
                                    background: 'rgba(255,255,255,.92)',
                                    borderRadius: 16,
                                    minWidth: mobile ? 80 : 110,
                                    padding: mobile ? '11px 8px' : '14px 17px',
                                    marginBottom: mobile ? 2 : 0,
                                    marginRight: 2,
                                    boxShadow: '0 2px 14px #b1bee733',
                                    textAlign: 'center',
                                    fontWeight: 600
                                }}>
                                <div style={{ fontWeight: 700, fontSize: mobile ? 16 : 22, color: '#2073cd', marginBottom: 1, marginTop: 2 }}>{stat.value}</div>
                                <div style={{ fontSize: mobile ? 11 : 15, color: '#7094b2' }}>{stat.label}</div>
                            </motion.div>
                        )}
                    </div>
