import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import HomePage from "./pages/HomePage";
import AboutPage from "./pages/AboutPage";
import WorkPage from "./pages/WorkPage";
import PortfolioPage from "./pages/PortfolioPage";
import ContactPage from "./pages/ContactPage";
import InteractionDesignCase from "./pages/InteractionDesignCase";

// Responsive device hook
function useIsMobile(breakpoint = 600) {
  const [isMobile, setIsMobile] = React.useState(window.innerWidth < breakpoint);
  React.useEffect(() => {
    function handleResize() { setIsMobile(window.innerWidth < breakpoint); }
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [breakpoint]);
  return isMobile;
}

// Minimal responsive navbar
function Navbar() {
  const location = useLocation();
  const isMobile = useIsMobile();
  const navs = [
    { to: "/", label: "Home" },
    { to: "/about", label: "About" },
    { to: "/work", label: "Work" },
    { to: "/portfolio", label: "Portfolio" },
    { to: "/contact", label: "Contact" }
  ];
  return (
    <nav
      style={{
        width: "100%",
        background: "#fff",
        boxShadow: "0 2px 13px #d2ddec22",
        padding: isMobile ? "11px 0 13px" : "20px 0 15px",
        display: "flex",
        justifyContent: isMobile ? "flex-start" : "center",
        position: "sticky",
        top: 0,
        gap: isMobile ? 12 : 42,
        fontWeight: 700,
        fontSize: isMobile ? 15 : 19,
        zIndex: 99,
        overflowX: isMobile ? "auto" : "visible"
      }}
    >
      {navs.map(({ to, label }) => (
        <Link
          key={to}
          to={to}
          style={{
            color: location.pathname === to ? "#7B9ACC" : "#232751",
            textDecoration: "none",
            borderRadius: 19,
            padding: isMobile ? "7px 8vw" : "8px 17px",
            opacity: location.pathname === to ? 1.0 : 0.82,
            fontWeight: 800,
            whiteSpace: "nowrap",
            fontSize: isMobile ? 15.5 : 19
          }}
        >
          {label}
        </Link>
      ))}
    </nav>
  );
}

// Main app
export default function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/about" element={<AboutPage />} />
        <Route path="/work" element={<WorkPage />} />
        <Route path="/portfolio" element={<PortfolioPage />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path="/interaction-design-case" element={<InteractionDesignCase />} />
      </Routes>
    </Router>
  );
}
