import React, { useState } from "react";

const projects = [
  {
    id: 1,
    title: "IoTBay - IoT Devices Ordering Application",
    subtitle: "Java Web App for managing and ordering IoT devices online",
    image: "https://images.unsplash.com/photo-1540350394557-8d14678e7f91?w=600&h=350&fit=crop",
    details: `IoTBay is an online platform for customers to purchase IoT devices like sensors, actuators, and gateways. I led the shipment management module, implementing full CRUD operations and secure data handling. The app features user registration/login, device catalog, order/payment processing, and shipment tracking, all built with MVC architecture. Improved backend performance for faster order retrieval and collaborated in an Agile 6-person team using Git for version control.`,
    tech: ["Java", "JSP", "JDBC", "MVC", "MySQL"],
    github: "https://github.com/jaeyunjks/IOTBay.git",
  },
  {
    id: 2,
    title: "Interaction Design Project",
    subtitle: "Web app for collaborative task management | UX Case Study",
    image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=600&h=350&fit=crop",
    details: `Designed and tested collaborative task manager app following HCI principles; includes research, prototyping, and iterative usability testing.`,
    tech: ["Figma", "UX Research"],
    designProcess: [
      {
        step: "Journal Entry",
        detail: "Reflective initial problem definition",
        file: "/journal-entry.pdf"
      },
      {
        step: "Interview & Persona",
        detail: "Created personas from interview data",
        file: "/persona.pdf"
      },
      {
        step: "Storyboard",
        detail: "Narrative storyboard",
        file: "/storyboard.png"
      },
      {
        step: "Paper Prototype",
        detail: "Tested UI sketches",
        file: "/paperproto.pdf"
      },
      {
        step: "DIA Video",
        detail: "Final summary & rationale",
        file: "/dia-fid.mp4"
      }
    ]
  }
];

const accent = "#F5F7FA";
const cardBg = "#CAD3E2";
const cardAccent = "#7189A2";
const navy = "#22253B";
const dark = "#495464";

export default function App() {
  const [expandedId, setExpandedId] = useState(null);

  return (
    <div style={{ minHeight: "100vh", background: accent, color: navy, fontFamily: "Nunito, Arial, sans-serif" }}>
      <nav style={{
        width: "100%",
        background: cardAccent,
        padding: "24px 0 14px",
        display: "flex",
        justifyContent: "center",
        gap: 38,
        fontWeight: 700,
        fontSize: 19,
        position: "sticky", top: 0,
        zIndex: 99
      }}>
        {["Home", "Projects", "Contact"].map((nav, i) =>
          <a key={nav}
            href={`#${nav.toLowerCase()}`}
            style={{ color: navy, textDecoration: "none", opacity: 0.87, borderRadius: 16, padding: "6px 15px" }}>
            {nav}
          </a>
        )}
      </nav>

      <main style={{ maxWidth: 850, margin: "38px auto", padding: "0 26px" }}>
        {/* Hero Section */}
        <section id="home" style={{ marginBottom: 50 }}>
          <h1 style={{ fontSize: 46, fontWeight: 800, marginBottom: 10, color: dark }}>Yafie Farabi</h1>
          <div style={{ fontSize: 18, color: navy, opacity: .8, marginBottom: 12 }}>
            Software Engineer & Business Analyst | IT Student | 2022‚Äì2025
          </div>
          <p style={{ fontSize: 17, color: navy, lineHeight: 1.6, maxWidth: 550 }}>
            Passionate about web development, data, and designing impactful digital products. Now seeking IT internship opportunities to apply and grow my skillset.
          </p>
          <div style={{ marginTop: 20, display: "flex", gap: 10, alignItems: "center" }}>
            <a href="mailto:yafie.farabi@email.com"
              style={{
                background: cardAccent, color: "#fff", borderRadius: 14, fontWeight: 700,
                padding: "8px 22px", textDecoration: "none"
              }}>Contact</a>
            <a href="https://linkedin.com/in/yafiefarabi" target="_blank" rel="noreferrer"
              style={{ color: dark, textDecoration: "underline", fontWeight: 600 }}>LinkedIn</a>
            <a href="https://github.com/yafiefarabi" target="_blank" rel="noreferrer"
              style={{ color: dark, textDecoration: "underline", fontWeight: 600 }}>GitHub</a>
          </div>
        </section>

        {/* Projects Section */}
        <section id="projects" style={{ marginBottom: 60 }}>
          <h2 style={{ fontWeight: 700, fontSize: 32, marginBottom: 20, color: dark }}>Projects</h2>
          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 34, maxWidth: 900 }}>
            {projects.map(project => (
              <ProjectCard key={project.id}
                project={project}
                expandedId={expandedId}
                setExpandedId={setExpandedId}
                cardBg={cardBg}
                cardAccent={cardAccent}
                navy={navy}
                dark={dark}
              />
            ))}
          </div>
        </section>

        {/* Contact Section (Footer) */}
        <section id="contact" style={{ fontSize: 18, color: dark, marginBottom: 32, marginTop: 40 }}>
          <div>
            üìß <b>Email:</b> <a href="mailto:yafie.farabi@email.com" style={{ color: dark }}>yafie.farabi@email.com</a>
          </div>
          <div>
            üíº <b>LinkedIn:</b> <a href="https://linkedin.com/in/yafiefarabi" style={{ color: dark }} target="_blank" rel="noreferrer">
              linkedin.com/in/yafiefarabi
            </a>
          </div>
          <div>
