// ----- Project Card (simple, calm aesthetic, all in one page) -----
function ProjectCard({ project, expandedId, setExpandedId, cardBg, cardAccent, navy, dark }) {
  const isExpanded = expandedId === project.id;

  return (
    <div style={{
      background: cardBg,
      borderRadius: 15,
      boxShadow: "0 2px 14px rgba(34,37,59,.07)",
      padding: 24,
      display: "flex",
      flexDirection: "column",
      transition: "box-shadow .22s",
      cursor: "pointer",
      border: isExpanded ? `2.4px solid ${cardAccent}` : `1.2px solid #eef`,
      position: "relative"
    }}
      onClick={() => setExpandedId(isExpanded ? null : project.id)}
    >
      <img src={project.image} alt={project.title} style={{ width: "100%", borderRadius: 12, marginBottom: 15, objectFit: "cover", maxHeight: 180 }} />
      <h3 style={{ fontWeight: 700, fontSize: 24, margin: "8px 0", color: dark }}>{project.title}</h3>
      <div style={{ fontSize: 16, fontWeight: 500, color: navy, marginBottom: 8 }}>{project.subtitle}</div>
      <div style={{ display: "flex", gap: 8, marginBottom: 10 }}>
        {project.tech.map(t => (
          <span key={t} style={{
            background: cardAccent,
            color: "#fff",
            borderRadius: 12,
            fontWeight: 600,
            padding: "4px 14px",
            fontSize: 14
          }}>{t}</span>
        ))}
      </div>
      <div style={{ marginBottom: 8, color: navy, opacity: .85 }}>
        <span>Click to {isExpanded ? "collapse" : "expand"} details</span>
      </div>

      {isExpanded && (
        <div style={{
          marginTop: 12,
          background: "#f6f8fd",
          color: dark,
          borderRadius: 10,
          boxShadow: "0 2px 12px rgba(34,37,59,.09)",
          fontSize: 16,
          padding: "18px 14px",
          position: "relative"
        }}>
          <div style={{ marginBottom: 14 }}>{project.details}</div>
          {/* IoTBay code link */}
          {project.github && (
            <div style={{ marginBottom: 14 }}>
              <a href={project.github} target="_blank" rel="noreferrer"
                style={{
                  background: navy, color: "#fff", borderRadius: 8,
                  textDecoration: "none", fontWeight: 700,
                  padding: "7px 18px"
                }}>View Code</a>
            </div>
          )}

          {/* Interaction Design extra process */}
          {project.designProcess && (
            <div style={{ marginTop: 12 }}>
              <h4 style={{ color: navy, fontWeight: 700, fontSize: 19, marginBottom: 8 }}>Design Process</h4>
              <ol style={{ paddingLeft: 17 }}>
                {project.designProcess.map((step, idx) => (
                  <li key={step.step} style={{ marginBottom: 14 }}>
                    <span style={{ fontWeight: 600 }}>{idx + 1}. {step.step}</span>:{" "}
                    <span>{step.detail}</span>
                    {step.file && (
                      <>
                        {
                          (step.file.endsWith(".png") || step.file.endsWith(".jpg") || step.file.endsWith(".jpeg")) ? (
                            <div><img src={step.file} alt={step.step} style={{ width: "95%", maxWidth: 250, borderRadius: 8, margin: "7px 0" }} /></div>
                          ) : step.file.endsWith(".pdf") ? (
                            <a href={step.file} target="_blank" rel="noreferrer"
                              style={{
                                background: cardAccent, color: "#fff", borderRadius: 7, textDecoration: "none",
                                fontWeight: 600, padding: "6px 13px", marginLeft: 8
                              }}>View PDF</a>
                          ) : step.file.endsWith(".mp4") ? (
                            <div>
                              <video src={step.file} controls style={{ width: "95%", maxWidth: 320, borderRadius: 8, marginTop: 10 }} />
                            </div>
                          ) : null
                        }
                      </>
                    )}
                  </li>
                ))}
              </ol>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
