import React from "react";

const WORKS = [
    {
        role: "Software Engineer (Intern)",
        company: "Amazing Tech Ltd.",
        period: "Feb 2025 – Aug 2025",
        location: "Jakarta, Indonesia (Hybrid)",
        summary: "Worked in a cross-functional team to develop internal dashboards, automate reporting, and optimize SQL queries for business data. Improved page load time by 23%.",
        stack: ["React", "Node.js", "SQL", "Jira"]
    },
    {
        role: "Web Development Project",
        company: "IoTBay University Team",
        period: "Aug 2024 – Nov 2024",
        location: "UNSW Sydney",
        summary: "Team lead for the shipment module, designed and developed secure CRUD backend for IoT device ordering system. Led code reviews and Git branching strategy.",
        stack: ["Java", "JSP", "MVC", "MySQL", "Git"]
    },
    {
        role: "Human-Computer Interaction Design",
        company: "University Individual Project",
        period: "Mar 2024 – Jun 2024",
        location: "Online/Remote",
        summary: "Designed and iterated on a collaborative task manager web UI, covering user research, paper prototype, storyboard, and usability testing.",
        stack: ["Figma", "UX Case Study", "React", "HTML/CSS"]
    }
];

export default function WorkPage() {
    return (
        <div style={{ maxWidth: 900, margin: "52px auto", padding: "0 15px" }}>
            <h1 style={{ fontWeight: 900, fontSize: 36, color: "#7B9ACC", marginBottom: 26 }}>Work & Project Experience</h1>
            <div style={{ display: "flex", flexDirection: "column", gap: 28 }}>
                {WORKS.map(work => (
                    <div key={work.role + work.company} style={{
                        background: "#EDF2FB", borderRadius: 14, padding: 27,
                        boxShadow: "0 2px 14px #c0c7ef15", marginBottom: 8
                    }}>
                        <div style={{ fontSize: 22, fontWeight: 800, color: "#232751" }}>{work.role}</div>
                        <div style={{ fontSize: 18, marginBottom: 6 }}>
                            <b>{work.company}</b> — <span style={{ color: "#7B9ACC", fontWeight: 600 }}>{work.period}</span>
                        </div>
                        <div style={{ color: "#6B7C8A", fontSize: 15, marginBottom: 8 }}>{work.location}</div>
                        <div style={{ fontSize: 17, lineHeight: 1.6, color: "#22253B", marginBottom: 12 }}>{work.summary}</div>
                        <div style={{ display: "flex", gap: 9 }}>
                            {work.stack.map(s => (
                                <span key={s} style={{
                                    background: "#D0DEFA", color: "#232751", borderRadius: 11,
                                    padding: "5px 15px", fontWeight: 700, fontSize: 15
                                }}>{s}</span>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
