import React, { useMemo, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// List of your steps, including multiple gdrive videos and Figma prototype
const CASE_STUDY_STEPS = [
    {
        title: "Journal Entry",
        desc: "Initial reflections and thoughts regarding daily challenges incorporating design principles.",
        file: "/journal-entry.pdf",
        type: "pdf"
    },
    {
        title: "Affinity Diagram",
        desc: "Visual mapping of core pain points facing the persona.",
        file: "/affinity-diagram.png",
        type: "image"
    },
    {
        title: "Persona",
        desc: "Full persona outlining user background, needs, and motivations.",
        file: "/persona.png",
        type: "image"
    },
    {
        title: "Problem Scenario",
        desc: "Description of daily time management struggles, work/life balance and burnout.",
        content: `I sit down in my chair and relax for a bit while I take in what I need to do for the day. ...`
    },
    {
        title: "Future Use Scenario",
        desc: "Design solution improves wellbeing and productivity.",
        content: `I start everyday at the same time, not too early in the morning because I can work from home, ...`
    },
    {
        title: "Storyboard",
        desc: "User flow visual showing solution in context.",
        file: "/storyboard.png",
        type: "image"
    },
    {
        title: "Specified Goal",
        desc: "User goal for prototype evaluation: setup a personalized sit/stand schedule and activity sensor preferences.",
        content: `To setup or import a personalized sit/stand schedule for the smart standing desk for the week, ...`
    },
    {
        title: "Paper Prototype",
        desc: "Low fidelity paper prototype for initial usability testing.",
        file: "/paperproto.jpg",
        type: "image"
    },
    {
        title: "DIA Video",
        desc: "High fidelity demonstration and evaluation video prototypes.",
        videos: [
            {
                label: "Desktop Prototype",
                url: "https://drive.google.com/file/d/1dSDjvmMtnqEoK_DzWYB28rvRATXTidnw/preview"
            },
            {
                label: "Mobile Prototype",
                url: "https://drive.google.com/file/d/1RkBTMXy4WDBAg8K3v9D4-4xgv9S9ogf_/preview"
            },
        ],
        type: "gdrive-multivideo"
    },
    {
        title: "Figma Mobile App Prototype",
        desc: "Interactive mobile app design, user testing in Figma.",
        file: "https://www.figma.com/proto/your-prototype-link",
        type: "figma"
    }
];

// Array of soft background colors
const colors = [
    "#C7D8F7", "#D1E1F4", "#F2F8FF", "#EDF2FB", "#EAF6FF", "#D2E6F7", "#F3F8FC", "#F7FAFF", "#DBEAF6"
];

// PDF Modal Popup
const PDFModal = ({ open, url, onClose }) => open && (
    <div style={{
        position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
        background: "rgba(60,80,120,0.19)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000
    }}>
        <motion.div
            initial={{ opacity: 0, scale: .88, y: 40 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.85, y: 60 }}
            transition={{ duration: 0.5 }}
            style={{
                background: "#fff", borderRadius: 18, boxShadow: "0 4px 24px #0f143058", padding: 22,
                width: "96vw", maxWidth: 620, maxHeight: "90vh", display: "flex", flexDirection: "column"
            }}
        >
            <button
                onClick={onClose}
                style={{
                    marginLeft: "auto", marginBottom: 9, background: "#7B9ACC",
                    color: "#fff", borderRadius: 7, border: "none", fontWeight: 700,
                    padding: "7px 16px", cursor: "pointer"
                }}
            >
                Close
            </button>
            <iframe
                src={url}
                title="PDF Viewer"
                style={{
                    width: "100%",
                    height: "70vh",
                    borderRadius: 10,
                    border: "none",
                    background: "#f8fbff"
                }}
            />
        </motion.div>
    </div>
);

// Main component
export default function InteractionDesignCase() {
    const refs = useMemo(() => CASE_STUDY_STEPS.map(() => React.createRef()), []);
    const [pdfModal, setPdfModal] = useState({ open: false, url: "" });

    const scrollToStep = idx => {
        refs[idx].current?.scrollIntoView({ behavior: "smooth", block: "start" });
    };

    return (
        <div style={{
            maxWidth: 820,
            margin: "48px auto",
            padding: 20,
            position: "relative"
        }}>
            {/* Animated title */}
            <motion.h1
                initial={{ opacity: 0, y: -24, scale: 0.93 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ duration: .8 }}
                style={{
                    fontSize: 34, color: "#7B9ACC", fontWeight: 900,
                    marginBottom: 22, letterSpacing: 0.5
                }}>
                Interaction Design Project
            </motion.h1>

            <motion.div
                initial={{ opacity: 0, y: -12 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: .4, duration: .6 }}
                style={{ marginBottom: 23, fontSize: 17 }}
            >
                Scroll to view each animated step, or click below to jump:
            </motion.div>

            {/* Step navigation bar */}
            <motion.nav
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: .6, duration: .5 }}
                style={{
                    display: "flex",
                    gap: 14,
                    flexWrap: "wrap",
                    marginBottom: 28,
                    padding: "6px 0"
                }}
            >
                {CASE_STUDY_STEPS.map((step, idx) => (
                    <motion.button
                        key={step.title}
                        whileHover={{ scale: 1.09, backgroundColor: "#E3EDFF", color: "#527DCA" }}
                        whileTap={{ scale: 0.97 }}
                        onClick={() => scrollToStep(idx)}
                        style={{
                            fontWeight: 600,
                            letterSpacing: 0.6,
                            color: "#7B9ACC",
                            background: "#F6FAFF",
                            border: "1.6px solid #D7E2FA",
                            borderRadius: 8,
                            padding: "7px 10px",
                            cursor: "pointer",
                            transition: "all .18s",
                            boxShadow: "0 1px 4px #c0c7ef0b"
                        }}
                    >
                        {step.title}
                    </motion.button>
                ))}
            </motion.nav>

            {/* PDF Modal: only shows when open */}
            <AnimatePresence>
                <PDFModal open={pdfModal.open} url={pdfModal.url} onClose={() => setPdfModal({ open: false, url: "" })} />
            </AnimatePresence>

            {/* Step by step animated cards */}
            {CASE_STUDY_STEPS.map((step, idx) => (
                <AnimatePresence key={step.title}>
                    <motion.section
                        ref={refs[idx]}
                        initial={{ opacity: 0, y: 40, scale: 0.97, boxShadow: "0 2px 26px #c0c7ef00" }}
                        whileInView={{
                            opacity: 1,
                            y: 0,
                            scale: 1,
                            boxShadow: "0 4px 26px #a2b6f444"
                        }}
                        whileHover={{
                            scale: 1.022,
                            boxShadow: "0 8px 28px #7b9acc22",
                            backgroundColor: colors[idx % colors.length]
                        }}
                        transition={{ delay: idx * 0.15, duration: 0.73, type: "spring" }}
                        viewport={{ once: true, amount: 0.19 }}
                        style={{
                            marginBottom: 38,
                            background: colors[idx % colors.length],
                            borderRadius: 14,
                            padding: 19,
                            boxShadow: "0 2px 13px #c0c7ef21",
                            cursor: "pointer"
                        }}>
                        <div style={{ fontWeight: 700, fontSize: 24, marginBottom: 6 }}>
                            {step.title}
                        </div>
                        <div style={{ marginBottom: 7, color: "#232751", fontSize: 17 }}>
                            {step.desc}
                        </div>
                        {/* Handle pdf modal */}
                        {step.file && step.type === "pdf" && (
                            <button
                                onClick={() => setPdfModal({ open: true, url: step.file })}
                                style={{
                                    background: "#7B9ACC",
                                    color: "#fff",
                                    borderRadius: 6,
                                    padding: "6px 15px",
                                    textDecoration: "none",
                                    fontWeight: 600,
                                    marginTop: 8,
                                    border: "none",
                                    cursor: "pointer"
                                }}>
                                View PDF
                            </button>
                        )}

                        {/* Handle images */}
                        {step.file && step.type === "image" && (
                            <motion.img
                                src={step.file} alt={step.title}
                                initial={{ opacity: 0, scale: .94 }}
                                whileInView={{ opacity: 1, scale: 1 }}
                                transition={{ duration: .73 }}
                                style={{
                                    width: "96%", maxWidth: 510,
                                    borderRadius: 10, marginTop: 8, boxShadow: "0 2px 18px #becae921"
                                }}
                            />
                        )}

                        {/* Handle multi Google Drive videos */}
                        {step.videos && step.type === "gdrive-multivideo" && (
                            <div style={{ display: "flex", gap: 20, flexWrap: "wrap" }}>
                                {step.videos.map((video, i) => (
                                    <motion.div
                                        key={video.label}
                                        initial={{ opacity: 0, scale: 0.96 }}
                                        whileInView={{ opacity: 1, scale: 1 }}
                                        transition={{ duration: .64, delay: i * 0.24 }}
                                        style={{
                                            width: 320, minWidth: 230, maxWidth: "100%",
                                            marginTop: 8,
                                            borderRadius: 10,
                                            overflow: "hidden",
                                            boxShadow: "0 2px 18px #becae921",
                                            background: "#f6f8fa",
                                            display: "flex",
                                            flexDirection: "column",
                                            alignItems: "center"
                                        }}
                                    >
                                        <div style={{ fontWeight: 600, margin: "10px 0 6px", color: "#527DCA" }}>
                                            {video.label}
                                        </div>
                                        <iframe
                                            src={video.url}
                                            allow="autoplay"
                                            frameBorder="0"
                                            style={{
                                                width: "100%",
                                                height: "210px",
                                                borderRadius: "10px"
                                            }}
                                            allowFullScreen
                                            title={video.label}
                                        />
                                    </motion.div>
                                ))}
                            </div>
                        )}

                        {/* Handle Figma embed */}
                        {step.file && step.type === "figma" && (
                            <motion.div
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                transition={{ duration: .72, delay: .13 }}
                                style={{
                                    width: "100%",
                                    minHeight: 400,
                                    marginTop: 12,
                                    borderRadius: 10,
                                    boxShadow: "0 2px 12px #c7d8f733",
                                    overflow: "hidden"
                                }}>
                                <iframe
                                    src={step.file}
                                    allowFullScreen
                                    width="100%"
                                    height="500"
                                    style={{
                                        border: "none",
                                        borderRadius: "10px",
                                        minHeight: 430,
                                        background: "#fff"
                                    }}
                                    title={step.title}
                                />
                            </motion.div>
                        )}

                        {/* Handle content */}
                        {step.content && (
                            <motion.div
                                initial={{ opacity: 0, y: 30 }}
                                whileInView={{ opacity: 1, y: 0 }}
                                transition={{ duration: .72, delay: .13 }}
                                style={{
                                    fontSize: 15,
                                    color: "#565",
                                    background: "#F4F8FC",
                                    margin: "12px 0 0",
                                    padding: 13,
                                    borderRadius: 9,
                                    boxShadow: "0 2px 7px #c7d8f711"
                                }}>
                                {step.content}
                            </motion.div>
                        )}
                    </motion.section>
                </AnimatePresence>
            ))}
        </div>
    );
}
