import React, { useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// --- Utility for smooth scroll to ref ---
function scrollToRef(ref) {
    if (ref && ref.current) ref.current.scrollIntoView({ behavior: "smooth" });
}

// --- Modal component for demo/diagram ---
function Modal({ open, onClose, children }) {
    if (!open) return null;
    return (
        <div style={{
            position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
            background: 'rgba(40,72,180,0.19)', display: 'flex',
            alignItems: 'center', justifyContent: 'center', zIndex: 99
        }}>
            <motion.div
                initial={{ opacity: 0, scale: .93, y: 40 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.87, y: 60 }}
                transition={{ duration: 0.45 }}
                style={{
                    background: 'rgba(255,255,255,0.98)',
                    borderRadius: 22,
                    maxWidth: 460, minHeight: 120,
                    boxShadow: '0 12px 34px #22333330',
                    padding: '34px 22px',
                    position: 'relative'
                }}>
                <button
                    style={{
                        position: 'absolute', top: 10, right: 19, fontSize: 27,
                        background: 'none', border: 'none', color: '#2073cd', fontWeight: 900, cursor: 'pointer'
                    }}
                    onClick={onClose}
                >Ã—</button>
                {children}
            </motion.div>
        </div>
    );
}

// --- Glass Card (no border, mesh background accent) ---
function GlassCard({ children, style = {}, ...rest }) {
    return (
        <motion.div
            initial={{ opacity: 0, y: 38 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.65, type: 'spring' }}
            viewport={{ once: true, amount: 0.18 }}
            style={{
                background: "rgba(255,255,255,0.89)",
                borderRadius: 18,
                boxShadow: "0 4px 19px #b1bee744",
                padding: "16px",
                margin: "0 0 22px 0",
                minWidth: 0,
                ...style
            }}
            {...rest}
        >
            {children}
        </motion.div>
    );
}

// --- Main Page ---
export default function EventManagementCaseStudy() {
    const [modalOpen, setModalOpen] = useState(false);
    const [archiModal, setArchiModal] = useState(null);

    // Ref for smooth scroll
    const projectOverviewRef = useRef();

    // Palette inspired by your page
    const mesh = "linear-gradient(120deg, #EAF6FF 0%, #D2E6F7 41%, #EDF2FB 100%)";
    const mobile = window.innerWidth <= 700;

    // Project Overview Features
