import React, { useState } from "react";

// Modal component
function Modal({ open, onClose, children }) {
    if (!open) return null;
    return (
        <div style={{
            position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
            background: 'rgba(50,70,180,0.12)', display: 'flex',
            alignItems: 'center', justifyContent: 'center', zIndex: 99
        }}>
            <div style={{
                background: 'rgba(255,255,255,0.85)',
                borderRadius: 18,
                maxWidth: 400,
                minHeight: 145,
                boxShadow: '0 14px 44px #22333329',
                padding: '30px 18px',
                position: 'relative',
                animation: 'fadeInModal .5s'
            }}>
                <button
                    style={{
                        position: 'absolute', top: 8, right: 15, fontSize: 26,
                        background: 'none', border: 'none', color: '#2d6dd2', fontWeight: 700, cursor: 'pointer'
                    }}
                    onClick={onClose}
                >Ã—</button>
                {children}
            </div>
            <style>{`
        @keyframes fadeInModal {
          from { opacity:0; transform: scale(.93);}
          to { opacity:1; transform: scale(1);}
        }
      `}</style>
        </div>
    );
}

// Glass card (simple)
function GlassCard({ children, style = {}, ...rest }) {
    return (
        <div
            style={{
                background: "rgba(255,255,255,0.72)",
                borderRadius: 18,
                boxShadow: "0 2px 22px #b1bee733",
                border: "1.5px solid #e4eafd",
                padding: "16px",
                margin: "0 0 18px 0",
                ...style
            }}
            {...rest}
        >
            {children}
        </div>
    );
}

// Section title utility
function SectionTitle({ subtitle, children }) {
    return (
        <div style={{ marginBottom: 13, marginTop: 5, textAlign: "center" }}>
            <div style={{ color: "#2d6dd2", fontWeight: 600, fontSize: 14, opacity: 0.79 }}>{subtitle}</div>
            <div style={{ fontWeight: 900, fontSize: 27, marginTop: 2 }}>{children}</div>
        </div>
    );
}

export default function EventManagementCaseStudy() {
    const [modalOpen, setModalOpen] = useState(false);

    // Animasi sederhana dengan fade-in dan hover scale
    return (
        <div style={{
            maxWidth: 940,
            margin: "40px auto 32px auto",
            padding: "2rem 0.5rem 3rem 0.5rem",
            background: "#fff",
            borderRadius: 32,
            boxShadow: "0 4px 43px #b1bee760, 0 1.5px 9px #7B9ACC24",
            minHeight: "80vh",
            fontFamily: 'Segoe UI, Arial, sans-serif'
        }}>
            {/* HERO */}
            <div style={{ textAlign: "center", marginBottom: 34 }}>
                <span style={{
                    display: "inline-block",
                    padding: "4px 17px",
                    borderRadius: 99,
                    background: "#e6efff",
                    color: "#2d6dd2",
                    fontWeight: 600,
                    fontSize: 13
                }}>Software Architecture Case Study</span>
                <h1 style={{
                    fontWeight: 900,
                    fontSize: 37,
                    color: "#2d6dd2",
                    margin: "17px 0 13px 0",
                    letterSpacing: ".5px"
                }}>Event Management & Ticketing Platform</h1>
                <div style={{
                    color: "#435a7d",
                    fontSize: 18,
                    marginBottom: 16
                }}>Modern scalable system for live events, real-time ticketing, analytics, and secure payments.</div>
                <div style={{ display: 'flex', justifyContent: 'center', gap: 12, marginBottom: 18 }}>
                    <button
                        style={{
                            background: "#2f69d6",
                            color: "#fff",
                            fontWeight: 700,
                            fontSize: 17,
                            borderRadius: 13,
                            padding: "10px 26px",
                            border: "none",
                            boxShadow: "0 3px 12px #2d6dd299",
                            cursor: "pointer",
                            transition: "transform .2s",
                            marginRight: 5
                        }}
                        onClick={() => setModalOpen(true)}
                        onMouseEnter={e => e.currentTarget.style.transform = "scale(1.09)"}
                        onMouseLeave={e => e.currentTarget.style.transform = "scale(1)"}
                    >
                        Explore Diagram
                    </button>
                    <button
                        style={{
                            background: "#fff",
                            color: "#2f69d6",
                            fontWeight: 600,
                            fontSize: 17,
                            borderRadius: 13,
                            padding: "10px 26px",
                            border: "2px solid #d5eaff",
                            boxShadow: "0 2px 12px #d8e8ff80",
                            cursor: "pointer",
                            transition: "transform .18s",
                        }}
                        onClick={() => setModalOpen(true)}
                        onMouseEnter={e => e.currentTarget.style.transform = "scale(1.06)"}
                        onMouseLeave={e => e.currentTarget.style.transform = "scale(1)"}
                    >
                        Watch Demo
                    </button>
                </div>
                {/* Quick facts */}
                <div style={{
                    display: 'flex', gap: 17, flexWrap: 'wrap',
                    justifyContent: 'center', marginBottom: 9
                }}>
                    {[
                        { label: "Microservices", value: "12+" },
                        { label: "Scale", value: "10K TPS" },
                        { label: "Uptime", value: "99.9%" },
                        { label: "Latency", value: "<100ms" }
                    ].map(stat =>
                        <GlassCard key={stat.label} style={{
                            padding: '14px 9px',
                            minWidth: 100,
                            textAlign: 'center',
                            fontWeight: 500,
                            transition: 'box-shadow .19s',
                        }}>
                            <div style={{ fontWeight: 700, fontSize: 25, color: '#2d6dd2', marginBottom: 2 }}>{stat.value}</div>
                            <div style={{ fontSize: 13, color: '#7095c7' }}>{stat.label}</div>
                        </GlassCard>
                    )}
                </div>
            </div>

            {/* MODAL for diagram/demo */}
            <Modal open={modalOpen} onClose={() => setModalOpen(false)}>
                <div style={{ textAlign: 'center' }}>
                    <div style={{
                        height: 120, borderRadius: 19,
                        background: 'linear-gradient(90deg,#e6efff,#d6f6ff)',
                        color: "#2964b5", fontWeight: 800, fontSize: 23,
                        display: 'flex', justifyContent: 'center', alignItems: 'center',
                        marginBottom: 11
                    }}>
                        [Animated Parallax/Diagram Placeholder]
                    </div>
                    <div style={{ color: '#2d6dd2', fontWeight: 700, fontSize: 17, marginTop: 10 }}>
                        Contoh animasi/diagram architecture
                    </div>
                </div>
            </Modal>

            {/* OVERVIEW */}
            <SectionTitle subtitle="Platform goals and key requirements">Project Overview</SectionTitle>
            <div style={{
                display: 'grid',
                gridTemplateColumns: '1fr 1fr',
                gap: 22,
                marginBottom: 32,
                maxWidth: 690,
                margin: '0 auto'
            }}>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Key Features:</span>
                    <ul style={{ marginLeft: 17, marginTop: 8, color: "#435a7d", fontSize: 15 }}>
                        <li>Multimedia support</li>
                        <li>Dynamic pricing, seat selection</li>
                        <li>Social media integration</li>
                        <li>Real-time ticket tracking</li>
                        <li>Secure payments (PCI DSS)</li>
                        <li>Post-event analytics</li>
                        <li>Mobile app experience</li>
                    </ul>
                </GlassCard>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Stakeholders:</span>
                    <ul style={{ marginLeft: 17, marginTop: 8, color: "#435a7d", fontSize: 15 }}>
                        <li>Event Organisers</li>
                        <li>Attendees</li>
                        <li>Payment Providers</li>
                        <li>Marketing Partners</li>
                    </ul>
                </GlassCard>
            </div>

            {/* PROBLEM */}
            <SectionTitle subtitle="Challenges & trade-offs">Problem Statement</SectionTitle>
            <GlassCard style={{ textAlign: "left", maxWidth: 680, margin: "0 auto 24px auto", fontSize: 15, lineHeight: 1.65 }}>
                <div>
                    Platform harus menangani lonjakan sales, struktur event fleksibel, pricing dinamis, inventori tiket real-time, analytics, dan pembayaran aman.
                </div>
                <div style={{ marginTop: 9 }}>
                    <b style={{ color: '#2d6dd2' }}>Trade-offs:</b>
                    <ul style={{ marginLeft: 17, marginTop: 7 }}>
                        <li>Consistency vs Availability (eventual utk inventory, strong utk payment)</li>
                        <li>Flexibility vs Performance (dynamic flows vs caching)</li>
                        <li>Security vs Usability (MFA vs kemudahan booking)</li>
                    </ul>
                </div>
            </GlassCard>

            {/* ARCHITECTURE */}
            <SectionTitle subtitle="Views & quality attributes">System Architecture</SectionTitle>
            <div style={{
                display: 'grid',
                gridTemplateColumns: '1fr 1fr',
                gap: 22,
                marginBottom: 32,
                maxWidth: 690,
                margin: '0 auto'
            }}>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Context View:</span>
                    <div style={{ marginTop: 7, color: "#345" }}>Boundaries & integration (users, payment, social, analytics)</div>
                </GlassCard>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Container View:</span>
                    <div style={{ marginTop: 7, color: "#345" }}>Stack: React web/app, API Gateway, microservices, serverless, clustered DB</div>
                </GlassCard>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Component View:</span>
                    <div style={{ marginTop: 7, color: "#345" }}>Modules: event creator, ticket engine, payment, notification, analytics</div>
                </GlassCard>
                <GlassCard>
                    <span style={{ fontWeight: 700, color: '#2d6dd2' }}>Deployment View:</span>
                    <div style={{ marginTop: 7, color: "#345" }}>Infra: Kubernetes, CDN, multi-reg DB, load balancer</div>
                </GlassCard>
            </div>
            <GlassCard style={{ textAlign: "left", maxWidth: 680, margin: "0 auto 28px auto", fontSize: 15 }}>
                <b style={{ color: '#2d6dd2' }}>Quality Attributes:</b>
                <ul style={{ marginLeft: 17, marginTop: 7 }}>
                    <li>Performance: &lt;100ms API</li>
                    <li>Security: PCI DSS, Encryption</li>
                    <li>Availability: 99.9% uptime</li>
                    <li>Reliability: Multi-region, auto backup</li>
                </ul>
            </GlassCard>

            {/* PATTERNS */}
            <SectionTitle subtitle="Recommended patterns & main components">Patterns & Components</SectionTitle>
            <div style={{
                display: 'grid',
                gridTemplateColumns: '1fr 1fr',
                gap: 22,
                marginBottom: 32,
                maxWidth: 690,
                margin: '0 auto'
            }}>
                <GlassCard>
                    <b style={{ color: '#2d6dd2' }}>Microservices:</b>
                    <div style={{ marginTop: 7, color: "#345" }}>Scalable, decoupled domain logic</div>
                </GlassCard>
                <GlassCard>
                    <b style={{ color: '#2d6dd2' }}>Event-driven/CQRS:</b>
                    <div style={{ marginTop: 7, color: "#345" }}>Async flows, separation of write/read</div>
                </GlassCard>
                <GlassCard>
                    <b style={{ color: '#2d6dd2' }}>API Gateway & BFF:</b>
                    <div style={{ marginTop: 7, color: "#345" }}>Optimized APIs for frontend, mobile, security/traffic</div>
                </GlassCard>
                <GlassCard>
                    <b style={{ color: '#2d6dd2' }}>Serverless:</b>
                    <div style={{ marginTop: 7, color: "#345" }}>Autoscale for spikes, cut ops cost</div>
                </GlassCard>
            </div>
            <GlassCard style={{ textAlign: "left", maxWidth: 680, margin: "0 auto 28px auto", fontSize: 15 }}>
                <b style={{ color: '#2d6dd2' }}>Component Map:</b>
                <ul style={{ marginLeft: 17, marginTop: 7 }}>
                    <li>Event Management Service</li>
                    <li>Ticketing Engine</li>
                    <li>Payment Gateway Connector</li>
                    <li>Notification / Analytics Modules</li>
                    <li>Authentication</li>
                </ul>
            </GlassCard>

            {/* CONTRIBUTION */}
            <SectionTitle subtitle="My role, results, reflection">My Contribution & Results</SectionTitle>
            <GlassCard style={{ textAlign: "left", maxWidth: 680, margin: "0 auto 10px auto", fontSize: 15 }}>
                <ul style={{ marginLeft: 8 }}>
                    <li><b>Role:</b> Architected all major views, build ticket/payment microservices, guide team, stakeholder mapping</li>
                    <li><b>Results:</b> 10K+ users, sub-100ms API, PCI DSS, 99.9% uptime, scalable infra</li>
                    <li><b>Reflection:</b> Improved trade-offs via API contract, serverless for scale, security+UX balance</li>
                </ul>
            </GlassCard>

            {/* Extra: Simple fade-in animation for cards */}
            <style>{`
        .glass-card {
          animation: fadeInCard 0.8s cubic-bezier(.16,.68,.43,1) 1;
        }
        @keyframes fadeInCard {
          0% {opacity:0;transform:translateY(38px);}
          80% {opacity:.9;}
          100% {opacity:1;transform:translateY(0);}
        }
      `}</style>
        </div>
    );
}
